body_width = 20;
head_size = 15;

// Create the tokoloshe figure
module tokoloshe() {
    color(body_color) {
        // Main body - slightly hunched
        translate([0, 0, 0]) {
            // Torso
            difference() {
                cube([body_width, body_width * 0.8, body_height], center = true);
                // Hollow interior for printing
                cube([body_width - 4, body_width * 0.8 - 4, body_height - 2], center = true);
            }

            // Pot belly protrusion
            translate([0, -body_width * 0.4 - 3, -body_height * 0.2]) {
                sphere(d = body_width * 0.8);
            }
        }

        // Head
        translate([0, 0, body_height * 0.8]) {
            difference() {
                sphere(d = head_size);
                // Eye sockets
                translate([head_size * 0.25, -head_size * 0.4, head_size * 0.25]) {
                    sphere(d = head_size * 0.3);
                }
                translate([-head_size * 0.25, -head_size * 0.4, head_size * 0.25]) {
                    sphere(d = head_size * 0.3);
                }
            }

            // Eyes glowing red
            color(eye_color) {
                translate([head_size * 0.25, -head_size * 0.45, head_size * 0.25]) {
                    sphere(d = head_size * 0.15);
                }
                translate([-head_size * 0.25, -head_size * 0.45, head_size * 0.25]) {
                    sphere(d = head_size * 0.15);
                }
            }
        }

        // Arms
        for(side = [-1, 1]) {
            translate([side * body_width * 0.7, 0, body_height * 0.3]) {
                rotate([0, 0, side * 30]) {
                    // Upper arm
                    cylinder(h = body_height * 0.4, d = body_width * 0.25, center = true);
                    translate([0, -body_height * 0.2, 0]) {
                        // Forearm
                        rotate([90, 0, 0]) {
                            cylinder(h = body_height * 0.3, d = body_width * 0.2, center = true);
                        }
                    }
                }
            }
        }

        // Legs - short and bowed
        for(side = [-1, 1]) {
            translate([side * body_width * 0.3, 0, -body_height * 0.6]) {
                // Thigh
                cylinder(h = body_height * 0.35, d = body_width * 0.3, center = true);
                translate([0, 0, -body_height * 0.35]) {
                    // Lower leg
                    cylinder(h = body_height * 0.3, d = body_width * 0.25, center = true);
                }
            }
        }

        // Feet - oversized
        translate([0, body_width * 0.5, -body_height * 0.9]) {
            cube([body_width * 1.2, body_width * 0.6, body_width * 0.3], center = true);
        }
    }

    // Wild hair tufts
    color(hair_color) {
        for(angle = [0 : 45 : 359]) {
            translate([0, 0, body_height * 0.8 + head_size * 0.4]) {
                rotate([0, 0, angle]) {
                    translate([head_size * 0.3, 0, 0]) {
                        cylinder(h = head_size * 0.8, d = head_size * 0.15, center = false);
                    }
                }
            }
        }
    }

    // Sharp teeth
    color("Ivory") {
        for(i = [-2 : 2]) {
            translate([i * head_size * 0.1, -head_size * 0.45, body_height * 0.8 - head_size * 0.2]) {
                cube([head_size * 0.08, head_size * 0.1, head_size * 0.2], center = true);
            }
        }
    }

    // Claws on hands
    color("Ivory") {
        for(side = [-1, 1]) {
            translate([side * body_width * 0.7, -body_height * 0.5, body_height * 0.15]) {
                rotate([0, 0, side * 30]) {
                    for(i = [-0.5, 0, 0.5]) {
                        translate([i * body_width * 0.1, -body_height * 0.35, 0]) {
                            rotate([90, 0, 0]) {
                                cylinder(h = body_width * 0.15, d1 = body_width * 0.05, d2 = 0, center = false);
                            }
                        }
                    }
                }
            }
        }
    }

    // Water droplets (magical essence)
    color("DodgerBlue", 0.6) {
        for(i = [1 : 12]) {
            rotate([0, 0, i * 30]) {
                translate([body_width * 0.8, 0, body_height * 0.3 - i * 3]) {
                    sphere(d = 2);
                }
            }
        }
    }
}

