color_teeth = "#FFF8DC";     // Ivory teeth

// Overall scale for 3D printing
scale_factor = 0.8;

// Main body
module tikoloshe() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Head
            translate([0, 0, 35]) {
                color(color_skin) {
                    // Skull shape
                    sphere(r = 8);

                    // Elongated snout
                    translate([0, -6, -2]) {
                        scale([0.8, 1.2, 0.6]) {
                            sphere(r = 6);
                        }
                    }

                    // Pointed ears
                    translate([-7, 0, 5]) {
                        rotate([0, 0, -30]) {
                            cylinder(h = 8, r1 = 3, r2 = 0.1);
                        }
                    }
                    mirror([1, 0, 0]) {
                        translate([-7, 0, 5]) {
                            rotate([0, 0, -30]) {
                                cylinder(h = 8, r1 = 3, r2 = 0.1);
                            }
                        }
                    }
                }

                // Eyes
                translate([4, -2, 2]) {
                    color(color_eyes) {
                        sphere(r = 1.5);
                    }
                }
                mirror([1, 0, 0]) {
                    translate([4, -2, 2]) {
                        color(color_eyes) {
                            sphere(r = 1.5);
                        }
                    }
                }

                // Sharp teeth
                for(i = [-2:2]) {
                    translate([i*1.2, -9, -3]) {
                        rotate([90, 0, 0]) {
                            color(color_teeth) {
                                cylinder(h = 2, r1 = 0.5, r2 = 0.1);
                            }
                        }
                    }
                }
            }

            // Torso
            translate([0, 0, 20]) {
                color(color_skin) {
                    scale([1.2, 0.8, 1.5]) {
                        sphere(r = 10);
                    }
                }
            }

            // Arms
            arm();
            mirror([1, 0, 0]) arm();

            // Legs
            leg();
            translate([5, 0, 0]) leg();

            // Tail
            translate([0, 8, 15]) {
                rotate([0, 30, 0]) {
                    color(color_skin) {
                        for(i = [0:5]) {
                            translate([0, 0, -i*3]) {
                                rotate([i*5, 0, 0]) {
                                    sphere(r = 4 - i*0.5);
