// Boitat - the fire serpent from Brazilian folklore
// A glowing snake with eyes of fire that protects the forests

// Overall scale for 3D printing
scale_factor = 0.4;

// Colors
body_color = "forestgreen";
eye_color = "red";
fire_color = "orange";

// Main module for the entire creature
module boitata() {
    scale([scale_factor, scale_factor, scale_factor])
    union() {
        // Serpent body
        translate([0, 0, 5]) 
        serpent_body();

        // Head
        translate([0, -35, 15]) 
        head();

        // Flames along the body
        flames();

        // Magical fire aura
        fire_aura();
    }
}

// Serpent body made of connected segments
module serpent_body() {
    color(body_color)
    union() {
        // Main body segments in an S-curve
        for(i = [0:5]) {
            angle = i * 30;
            x_offset = sin(angle) * 8;
            y_offset = -i * 6;
            z_offset = cos(angle) * 3;

            translate([x_offset, y_offset, z_offset])
            rotate([0, 0, angle])
            body_segment(i);
        }

        // Tail segments getting smaller
        for(i = [6:10]) {
            scale_factor = 1 - (i-5) * 0.15;
            angle = i * 35;
            x_offset = sin(angle) * 10;
            y_offset = -35 - (i-6) * 4;
            z_offset = cos(angle) * 2;

            translate([x_offset, y_offset, z_offset])
            scale([scale_factor, scale_factor, scale_factor])
            rotate([0, 0, angle])
            body_segment(i);
        }
    }
}

// Individual body segment with scales
module body_segment(index) {
    radius = 4 - index * 0.2;
    height = 5;

    union() {
        // Main segment
        cylinder(h=height, r=radius, $fn=16);

        // Scales around the segment
        for(j = [0:7]) {
            rotate([0, 0, j * 45])
            translate([radius * 0.8, 0, height/2])
            scale([0.3, 1, 0.4])
            sphere(r=1.5, $fn=8);
        }
    }
}

// Serpent head with features
module head() {
    color(body_color)
    union() {
        // Main head shape
        scale([1.2, 1.5, 1])
        sphere(r=6, $fn=24);

        // Snout
        translate([0, -8, 0])
        scale([0.8, 1, 0.7])
        sphere(r=4, $fn=16);

        // Eyes
        color(eye_color)
        translate([3, -2, 2])
        sphere(r=1.5, $fn=12);

        translate([-3, -2, 2])
        sphere(r=1.5, $fn=12);

        // Eye glow
        color(fire_color)
        translate([3.5, -2.5, 2.5])
        sphere(r=0.8, $fn=8);

        translate([-3.5, -2.5, 2.5])
        sphere(r=0.8, $fn=8);

        // Horns
        color("darkgreen")
        translate([2, 0, 4])
        rotate([30, 0, 30])
        cylinder(h=4, r1=0.8, r2=0.2, $fn=8);

        translate([-2, 0, 4])
        rotate([30, 0, -30])
        cylinder(h=4, r1=0.8, r2=0.2, $fn=8);

        // Fangs
        color("ivory")
        translate([1.5, -6, -1])
        rotate([0, 30, 0])
        cylinder(h=2, r1=0.5, r2=0.1, $fn=6);

        translate([-1.5, -6, -1])
        rotate([0, -30, 0])
        cylinder(h=2, r1=0.5, r2=0.1, $fn=6);
    }
}

// Flames along the body
module flames() {
    color(fire_color, 0.8)
    union() {
        // Flames on body
        for(i = [0:8]) {
            angle = i * 25;
            x_offset = sin(angle) * 8;
            y_offset = -i * 4.5;
            z_offset = 8 + cos(angle) * 2;

            translate([x_offset, y_offset, z_offset])
            rotate([0, 0, angle])
            flame();
        }

        // Larger flames on head
        translate([0, -35, 20])
        scale([1.5, 1.5, 2])
        flame();
    }
}

// Individual flame shape
module flame() {
    union() {
        // Main flame body
        scale([0.8, 1, 2])
        sphere(r=2, $fn=12);

        // Flame tips
        translate([0, 0, 3])
        scale([0.5, 0.5, 1])
        sphere(r=1.5, $fn=8);

        translate([0.5, 0, 2.5])
        scale([0.3, 0.3, 0.8])
        sphere(r=1, $fn=6);

        translate([-0.5, 0, 2.5])
        scale([0.3, 0.3, 0.8])
        sphere(r=1, $fn=6);
    }
}

// Magical fire aura around the creature
module fire_aura() {
    color(fire_color, 0.3)
    difference() {
        // Large glowing sphere
        sphere(r=25, $fn=32);

        // Cut out center
        sphere(r=20, $fn=32);
    }
}

