// Bennu Bird - Egyptian Phoenix
// 3D printable model with medium complexity realistic style

// Main parameters
wing_span = 40;
body_length = 25;
body_height = 15;
leg_length = 8;

// Colors
gold = [0.83, 0.69, 0.22];
crimson = [0.86, 0.08, 0.24];
onyx = [0.06, 0.06, 0.06];

// Body module
module body() {
    color(gold)
    union() {
        // Main body
        translate([0, 0, leg_length])
        scale([1.2, 1, 0.8])
        sphere(d=body_height);

        // Neck
        translate([0, body_length/3, leg_length+body_height/2])
        rotate([30, 0, 0])
        cylinder(h=body_length/2, d1=body_height/3, d2=body_height/4);

        // Tail feathers
        for(i=[-30:15:30]) {
            rotate([0, 0, i])
            translate([-body_length/2, 0, leg_length+body_height/4])
            rotate([120-abs(i), 0, 0])
            scale([0.3, 1, 0.1])
            cylinder(h=body_length/2, d=body_height/4);
        }
    }
}

// Head module
module head() {
    color(gold)
    union() {
        // Head
        translate([0, body_length/2+body_length/3, leg_length+body_height/2+body_length/2])
        rotate([40, 0, 0])
        scale([0.8, 1.2, 1])
        sphere(d=body_height/2.5);

        // Beak
        translate([0, body_length/2+body_length/3+body_height/3, leg_length+body_height/2+body_length/2])
        rotate([40, 0, 0])
        cylinder(h=body_height/3, d1=body_height/4, d2=0);

        // Eye
        color(onyx)
        translate([body_height/6, body_length/2+body_length/3+body_height/8, leg_length+body_height/2+body_length/2+body_height/8])
        sphere(d=body_height/12);

        // Crest feathers
        color(crimson)
        for(i=[-20:10:20]) {
            rotate([0, 0, i])
            translate([0, body_length/2+body_length/3, leg_length+body_height/2+body_length/2+body_height/4])
            rotate([90-i, 0, 0])
            cylinder(h=body_height/3, d=body_height/10);
        }
    }
}

// Wing module
module wing(side) {
    color(gold)
    mirror([side, 0, 0])
    translate([body_height/2, 0, leg_length+body_height/2])
    rotate([0, 0, side*20])
    union() {
        // Primary wing structure
        scale([1, wing_span/2, 0.3])
        sphere(d=body_height);

        // Wing feathers
        for(i=[0:20:80]) {
            rotate([0, 0, side*i])
            translate([0, wing_span/4, 0])
            rotate([0, 0, side*30])
            scale([0.5, 1.5, 0.1])
            cylinder(h=wing_span/3, d=body_height/4);
        }

        // Secondary feathers
        color(crimson)
        for(j=[10:20:70]) {
            rotate([0, 0, side*j])
            translate([body_height/4, wing_span/3, 0])
            rotate([0, 0, side*15])
            scale([0.3, 1, 0.05])
            cylinder(h=wing_span/4, d=body_height/5);
        }
    }
}

// Leg module
module leg(side) {
    color(onyx)
    union() {
        // Upper leg
        translate([side*body_height/3, 0, 0])
        rotate([0, side*10, 0])
        cylinder(h=leg_length*0.6, d=body_height/6);

        // Lower leg
        translate([side*body_height/3, 0, leg_length*0.6])
        rotate([0, side*5, 0])
        cylinder(h=leg_length*0.4, d=body_height/8);

        // Foot
        translate([side*body_height/3, 0, leg_length])
        rotate([90, 0, side*45])
        scale([1, 1.5, 0.5])
        cylinder(h=body_height/8, d=body_height/10);
    }
}

// Assemble the Bennu
union() {
    body();
    head();
    wing(1);
    wing(-1);
    leg(1);
    leg(-1);

    // Sun disk behind head
    color(crimson)
    translate([0, body_length/2+body_length/3+body_height/4, leg_length+body_height/2+body_length/2])
    rotate([90, 0, 0])
    cylinder(h=1, d=body_height/2);

    // Magical aura rings
    color([1, 0.84, 0])
    for(i=[1:0.5:3]) {
        translate([0, 0, leg_length+body_height/2])
        rotate_extrude()
        translate([i*body_height/2, 0, 0])
        circle(d=body_height/20);
    }
}

// Apep  Egyptian chaos serpent
// Realistic, medium-detail, 3D-printable ( 85 mm long, 30 mm high)

$fn = 48;          // reasonable resolution for FDM
wall = 1.2;        // 30.4 mm nozzle wall thickness
scale = 1.0;       // global scale, change to fit printer

// ----- colors -----
colorSnake  = "darkolivegreen";
colorBelly   = "darkgoldenrod";
colorEyes    = "crimson";
colorTeeth   = "ivory";
colorSpines  = "black";

module apep() {
    union() {
        // ---- central serpentine body ----
        color(colorSnake)
        hull() {
            translate([0,0,0]) sphere(6);
            translate([70,0,0]) sphere(5);
        }

        // ---- segmented body rings ----
        for (i=[1:5:65])
            translate([i,0,0])
                rotate([0,90,0])
                    cylinder(h=wall, r1=6-i/30, r2=6-i/30);

        // ---- belly plates ----
        color(colorBelly)
        for (i=[5:10:65])
            translate([i,0,-4])
                rotate([90,0,0])
                    cylinder(h=wall, r1=4.5, r2=2);

        // ---- dorsal spines ----
        color(colorSpines)
        for (i=[10:8:60])
            translate([i,0,7])
                rotate([0,20*sin(i*5),0])
                    cylinder(h=8, r1=wall, r2=0.5);

        // ---- head ----
        translate([75,0,0])
            union() {
                // snout
                color(colorSnake)
                cylinder(h=10, r1=5, r2=3);

                // eyes
                color(colorEyes)
                for (s=[-1,1])
                    translate([2, s*3, 2])
                        sphere(1.5);

                // curved fangs
                color(colorTeeth)
                for (s=[-1,1])
                    translate([0, s*2.5, -2])
                        rotate([0,30*s,0])
                            cylinder(h=4, r1=1.2, r2=0.3);
            }

        // ---- forked tongue ----
        color(colorTeeth)
        translate([85,0,-1])
            union() {
                for (s=[-1,1])
                    translate([0,s*1.5,0])
                        rotate([0,0,15*s])
                            cylinder(h=6, r1=0.8, r2=0.3);
            }
    }
}

// scale to printable size
scale([scale,scale,scale]) apep();

// Mythical Dragon Hatchling Set - Realistic Style
// Dimensions: ~80mm long, 65mm wide, 70mm tall

// Color definitions
egg_color = "LightYellow";
dragon_color = "DarkOliveGreen";
wing_color = "SaddleBrown";
eye_color = "FireBrick";
claw_color = "SlateGray";

// Main module
module dragon_hatchling_set() {
    union() {
        // Dragon egg
        color(egg_color)
        translate([0, 0, 0])
            dragon_egg();

        // Hatchling dragon
        color(dragon_color)
        translate([0, 0, 35])
            dragon_body();

        // Wings
        color(wing_color)
        union() {
            translate([15, 0, 45])
                rotate([0, 0, 20])
                left_wing();
            translate([-15, 0, 45])
                rotate([0, 0, -20])
                mirror([1, 0, 0])
                left_wing();
        }
    }
}

// Dragon egg with cracks
module dragon_egg() {
    difference() {
        // Main egg shape
        scale([1, 1, 1.3])
        sphere(d = 50, $fn = 50);

        // Crack lines
        for(i = [0:45:315]) {
            rotate([0, 0, i])
            translate([0, 22, 0])
            cube([1, 5, 65], center = true);
        }

        // Hole for dragon to emerge
        translate([0, 0, 30])
        scale([1.2, 1.2, 0.5])
        sphere(d = 30, $fn = 30);
    }
}

// Dragon body
module dragon_body() {
    union() {
        // Torso
        scale([1, 0.8, 1.2])
        sphere(d = 25, $fn = 30);

        // Head
        translate([0, 0, 20])
        scale([0.8, 0.7, 0.9])
        sphere(d = 20, $fn = 25);

        // Snout
        translate([0, 0, 27])
        scale([0.4, 0.3, 0.5])
        sphere(d = 15, $fn = 20);

        // Horns
        for(i = [0:1]) {
            rotate([0, 0, i * 20 - 10])
            translate([0, 7, 25])
            rotate([30, 0, 0])
            cylinder(h = 12, d1 = 3, d2 = 1, $fn = 8);
        }

        // Eyes
        color(eye_color)
        union() {
            translate([6, 5, 22])
            sphere(d = 3, $fn = 10);
            translate([-6, 5, 22])
            sphere(d = 3, $fn = 10);
        }

        // Legs
        for(side = [-1, 1]) {
            translate([side * 8, 0, 0])
            rotate([0, side * 15, 0])
            union() {
                // Upper leg
                cylinder(h = 12, d = 5, $fn = 8);
                // Lower leg
                translate([0, 0, 12])
                rotate([30, 0, 0])
                cylinder(h = 10, d = 4, $fn = 8);
                // Foot
                color(claw_color)
                translate([0, 0, 22])
                rotate([60, 0, 0])
                union() {
                    cylinder(h = 8, d = 4, $fn = 8);
                    // Toes
                    for(toe = [-1, 0, 1]) {
                        translate([toe * 2, 0, 4])
                        rotate([0, toe * 20, 0])
                        cylinder(h = 4, d1 = 2, d2 = 1, $fn = 6);
                    }
                }
            }
        }

        // Tail
        translate([0, -12, 5])
        rotate([0, 20, 0])
        union() {
            cylinder(h = 25, d1 = 8, d2 = 4, $fn = 12);
            translate([0, -13, 0])
            scale([1, 1, 0.3])
            sphere(d = 8, $fn = 12);
        }
    }
}

// Dragon wing
module left_wing() {
    difference() {
        // Main wing membrane
        hull() {
            translate([0, 0, 0])
            sphere(d = 3, $fn = 8);
            translate([20, 0, 5])
            sphere(d = 3, $fn = 8);
            translate([0, 0, 20])
            sphere(d = 3, $fn = 8);
            translate([25, 0, 10])
            sphere(d = 3, $fn = 8);
        }

        // Wing bones
        for(i = [1:3]) {
            rotate([0, 0, i * 25])
            translate([0, -2, i * 2])
            cube([30, 4, 2]);
        }
    }
}

// Generate the complete model
dragon_hatchling_set();

// Serpopard - Mythical leopard with serpent-like neck
// Medium complexity, realistic style
// 3D printable at ~80mm length

// Colors
color("darkgoldenrod") {

// Main body
union() {
    // Leopard body
    difference() {
        // Body core
        union() {
            // Torso
            translate([0, 0, 12])
                scale([1.5, 0.8, 0.6])
                sphere(15, $fn=32);

            // Hindquarters
            translate([-20, 0, 10])
                scale([1.2, 0.9, 0.7])
                sphere(12, $fn=32);

            // Chest
            translate([18, 0, 10])
                scale([1.1, 0.85, 0.65])
                sphere(10, $fn=32);
        }

        // Leg sockets
        for(i=[-1,1]) {
            // Front legs
            translate([12, 8*i, 8])
                rotate([0, 90, 0])
                cylinder(h=8, r=4, $fn=16);

            // Back legs
            translate([-12, 8*i, 8])
                rotate([0, 90, 0])
                cylinder(h=8, r=4, $fn=16);
        }
    }

    // Legs
    module leg(x, z, angle) {
        translate([x, 0, z])
            rotate([angle, 0, 0])
            union() {
                // Upper leg
                cylinder(h=12, r=3, $fn=12);
                // Lower leg
                translate([0, 0, -12])
                    cylinder(h=10, r=2.5, $fn=12);
                // Paw
                translate([0, 0, -22])
                    scale([1.2, 1, 0.8])
                    sphere(3, $fn=12);
            }
    }

    // Create legs
    leg(12, 8, 30);
    leg(12, -8, 30);
    leg(-12, 8, -30);
    leg(-12, -8, -30);

    // Serpent neck
    module neck_segment(z, scale_factor) {
        translate([0, 0, z])
            scale([scale_factor, scale_factor, 1])
            cylinder(h=5, r1=8*scale_factor, r2=6*scale_factor, $fn=16);
    }

    // Build curved neck
    for(i=[0:12]) {
        angle = i * 15;
        length = 25 + i * 3;
        neck_segment(25 + i*4, 0.9 - i*0.05);
        translate([length*cos(angle), 0, 25 + i*4 + length*sin(angle)])
            rotate([0, -angle, 0])
            scale([0.9 - i*0.05, 0.9 - i*0.05, 1])
            cylinder(h=5, r1=6*(0.9-i*0.05), r2=5*(0.9-i*0.05), $fn=16);
    }

    // Leopard head at end of neck
    translate([55, 0, 45])
        scale([0.5, 0.5, 0.5])
        union() {
            // Head
            sphere(8, $fn=24);

            // Snout
            translate([6, 0, -2])
                scale([1.2, 0.8, 0.6])
                sphere(5, $fn=16);

            // Ears
            for(x=[-3, 3]) {
                translate([x, 0, 6])
                    rotate([0, 30*sign(x), 0])
                    scale([0.3, 1, 1])
                    cylinder(h=4, r1=2, r2=1, $fn=8);
            }

            // Eyes
            color("black") {
                for(x=[-2.5, 2.5]) {
                    translate([x, 3, 1])
                        sphere(1, $fn=8);
                }
            }

            // Nose
            color("black")
                translate([8, 0, -2])
                    sphere(1.5, $fn=8);
        }

    // Tail
    color("darkgoldenrod") {
        translate([-35, 0, 12])
            rotate([0, 0, -20])
            for(i=[0:8]) {
                scale([0.9 - i*0.08, 0.9 - i*08, 1])
                translate([i*-3, 0, 0])
                    rotate([0, 0, i*5])
                    cylinder(h=4, r1=3.5 - i*0.3, r2=3 - i*0.3, $fn=12);
            }
    }

    // Spots pattern
    color("saddlebrown") {
        for(pos=[[5, 5, 15], [-5, 7, 14], [0, -6, 16], 
                [-10, 4, 13], [8, -3, 14], [-15, -2, 11]]) {
            translate(pos)
                scale([1, 1.5, 0.5])
                sphere(1.5, $fn=8);
        }
    }

    // Mane ridge along neck
    color("goldenrod") {
        for(i=[0:12]) {
            angle = i * 15;
            length = 25 + i * 3;
            translate([length*cos(angle), 0, 27 + i*4 + length*sin(angle)])
                rotate([0, -angle-90, 0])
                scale([0.5, 0.1, 1])
                cylinder(h=3, r1=2, r2=1, $fn=8);
        }
    }
}
}

