// Sekhmet - Egyptian Lioness Goddess
// Realistic style with medium complexity

$fn=32;

// Overall scale for 3D printing
overall_scale = 0.8;

// Main module for the complete Sekhmet
module sekhmet() {
    scale([overall_scale, overall_scale, overall_scale])
    union() {
        // Body and legs
        translate([0, 0, 20]) {
            body();
            legs();
        }

        // Head and crown
        translate([0, -35, 45]) {
            head();
            solar_disk_crown();
        }

        // Arms and staff
        translate([0, -15, 40]) {
            arms();
            staff();
        }
    }
}

// Lioness body
module body() {
    color("darkgoldenrod")
    union() {
        // Main torso
        scale([1.2, 2.5, 1.5])
        sphere(15);

        // Chest
        translate([0, 8, 0])
        scale([1.1, 0.8, 1.2])
        sphere(12);

        // Tail
        translate([0, 28, -5])
        rotate([0, -30, 0])
        cylinder(h=30, r1=3, r2=1);

        // Tail tuft
        translate([0, 55, -18])
        sphere(4);
    }
}

// Four legs
module legs() {
    color("goldenrod")
    for(x = [-12, 12]) {
        for(y = [-10, 15]) {
            translate([x, y, 0])
            leg();
        }
    }
}

// Single leg
module leg() {
    union() {
        // Upper leg
        translate([0, 0, 5])
        rotate([30, 0, 0])
        cylinder(h=20, r1=6, r2=4);

        // Lower leg
        translate([0, 10, -10])
        rotate([30, 0, 0])
        cylinder(h=18, r1=4, r2=3);

        // Paw
        translate([0, 18, -22])
        sphere(5);
    }
}

// Lioness head with human features
module head() {
    color("goldenrod")
    union() {
        // Main head
        scale([1.3, 1.5, 1.2])
        sphere(15);

        // Snout
        translate([0, -15, -2])
        scale([0.6, 0.8, 0.7])
        sphere(12);

        // Ears
        for(x = [-12, 12]) {
            translate([x, 0, 12])
            rotate([0, 20, 0])
            scale([0.4, 0.3, 0.8])
            cylinder(h=15, r1=8, r2=4);
        }

        // Nose
        color("black")
        translate([0, -20, -1])
        sphere(2);

        // Eyes
        for(x = [-6, 6]) {
            color("black")
            translate([x, -8, 5])
            scale([1, 0.5, 1])
            sphere(3);

            // Eye makeup
            color("teal")
            translate([x, -8, 5.5])
            scale([3, 0.5, 0.5])
            sphere(3);
        }

        // Mane ruff
        color("darkgoldenrod")
        for(i = [0:15]) {
            rotate([0, 0, i*24])
            translate([0, -5, -8])
            scale([0.3, 0.3, 1])
            cylinder(h=8, r1=3, r2=2);
        }
    }
}

// Solar disk crown
module solar_disk_crown() {
    color("gold")
    union() {
        // Solar disk
        translate([0, 0, 20])
        rotate([90, 0, 0])
        cylinder(h=3, r=12);

        // Uraeus (cobra)
        translate([0, 5, 20])
        rotate([0, 90, 0])
        scale([0.5, 0.5, 1])
        cylinder(h=15, r1=4, r2=2);

        // Cobra head
        translate([8, 5, 20])
        sphere(3);
    }
}

// Arms
module arms() {
    color("goldenrod")
    // Right arm with staff
    translate([15, 0, 0])
    rotate([0, 0, -20])
    arm();

    // Left arm
    mirror([1, 0, 0])
    translate([15, 0, 0])
    rotate([0, 0, -20])
    arm();
}

// Single arm
module arm() {
    union() {
        // Upper arm
        translate([0, 0, 5])
        rotate([40, 0, 20])
        cylinder(h=20, r1=5, r2=4);

        // Forearm
        translate([7, 0, -8])
        rotate([40, 0, 20])
        cylinder(h=18, r1=4, r2=3);

        // Hand
        translate([12, 0, -15])
        sphere(4);
    }
}

// Sekhem staff
module staff() {
    color("gold")
    translate([22, -5, -12])
    rotate([0, 0, 40])
    union() {
        // Staff handle
        cylinder(h=40, r=2);

        // Sekhem symbol top
        translate([0, 0, 40])
        rotate([90, 0, 0])
        linear_extrude(height=2)
        polygon(points=[[-5,0],[5,0],[5,8],[-5,8]]);
    }
}

// Generate the complete Sekhmet
sekhmet();

// Ammut - Devourer of the Dead (realistic style, medium complexity)
// Egyptian mythical creature with crocodile head, lion body, and hippopotamus legs

scale_factor = 0.4;  // Overall scale for 3D printing (fits ~80mm)

// Main colors
$fa = 1;
$fs = 0.4;

module ammut() {
    // Crocodile head
    color("darkolivegreen") {
        // Main head shape
        translate([0, 0, 12]) {
            scale([1.2, 0.8, 0.8]) sphere(8);
        }

        // Snout
        translate([7, 0, 10]) {
            rotate([0, -15, 0]) scale([1.8, 0.6, 0.5]) sphere(6);
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([2, side * 5, 15]) {
                color("black") sphere(1.5);
            }
        }

        // Teeth
        for(i = [0:5]) {
            translate([12 - i * 2, -2.5 + i * 1, 8]) {
                rotate([0, 20, 0]) cylinder(h = 3, r1 = 1, r2 = 0.3);
            }
            translate([12 - i * 2, 2.5 - i * 1, 8]) {
                rotate([0, 20, 0]) cylinder(h = 3, r1 = 1, r2 = 0.3);
            }
        }
    }

    // Lion body
    color("saddlebrown") {
        // Main torso
        translate([-8, 0, 8]) {
            scale([1.5, 1, 1.2]) sphere(10);
        }

        // Back ridge
        for(i = [0:4]) {
            translate([-15 + i * 5, 0, 16]) {
                cylinder(h = 4, r = 2);
            }
        }

        // Tail (crocodile-style)
        color("darkolivegreen") {
            translate([-22, 0, 8]) {
                rotate([0, 0, -30]) {
                    for(i = [0:6]) {
                        translate([-i * 3, 0, -i * 0.5]) {
                            scale([0.9 - i * 0.1, 0.9 - i * 0.1, 0.9 - i * 0.1]) 
                            sphere(3);
                        }
                    }
                }
            }
        }
    }

    // Hippopotamus legs
    color("dimgray") {
        // Front legs
        for(side = [-1, 1]) {
            translate([-5, side * 8, 0]) {
                cylinder(h = 12, r = 4);
            }
            // Feet
            translate([-5, side * 8, 0]) {
                scale([1.5, 2, 0.5]) sphere(3);
            }
        }

        // Back legs
        for(side = [-1, 1]) {
            translate([-18, side * 8, 0]) {
                cylinder(h = 12, r = 4.5);
            }
            // Feet
            translate([-18, side * 8, 0]) {
                scale([1.8, 2.2, 0.5]) sphere(3.5);
            }
        }
    }

    // Claws
    color("black") {
        for(side = [-1, 1]) {
            for(pos = [-5, -18]) {
                for(claw = [-1, 0, 1]) {
                    translate([pos, side * (7 + claw * 1.5), 0]) {
                        rotate([0, 90, 0]) 
                        cylinder(h = 2, r1 = 1, r2 = 0.3);
                    }
                }
            }
        }
    }
}

// Scale the entire model
scale(scale_factor) ammut();

// Uadjet - Winged Egyptian Cobra Deity
// Medium complexity realistic style

// Overall dimensions: ~70mm length

// Parameters
body_length = 50;
body_diameter = 8;
wing_span = 40;
head_diameter = 12;
scale_size = 1.5;

// Main module
module uadjet() {
    color("forestgreen") {
        // Body
        union() {
            // Main serpent body with scales
            for(i = [0:5:body_length-10]) {
                translate([i, 0, 0]) {
                    scale([1, 1, 0.8]) 
                        sphere(d=body_diameter);
                }
            }

            // Tapered tail
            for(i = [body_length-10:2:body_length]) {
                translate([i, 0, 0]) {
                    scale([1, 1, 0.6]) 
                        sphere(d=body_diameter * (body_length-i)/10);
                }
            }

            // Head
            translate([-8, 0, 0]) {
                sphere(d=head_diameter);

                // Snout
                translate([-6, 0, 0]) {
                    scale([2, 0.7, 0.7]) 
                        sphere(d=head_diameter/2);
                }

                // Eyes
                color("gold") {
                    translate([-3, 4, 3]) sphere(d=2);
                    translate([-3, -4, 3]) sphere(d=2);
                }

                // Hood flare
                color("darkgreen") {
                    translate([-2, 0, -2]) 
                        rotate([0, 90, 0])
                            cylinder(h=12, d1=16, d2=0);
                }
            }

            // Wings
            color("darkolive") {
                // Left wing
                translate([0, body_diameter/2, 0]) {
                    rotate([0, 0, -30]) {
                        difference() {
                            scale([0.1, wing_span/2, 15]) 
                                cube([1, 1, 1]);
                            // Wing membrane cutouts
                            for(j = [3:3:12]) {
                                translate([0, j, j-5]) 
                                    cube([2, 2, 2]);
                            }
                        }
                    }
                }

                // Right wing
                mirror([0, 1, 0]) {
                    translate([0, body_diameter/2, 0]) {
                        rotate([0, 0, -30]) {
                            difference() {
                                scale([0.1, wing_span/2, 15]) 
                                    cube([1, 1, 1]);
                                for(j = [3:3:12]) {
                                    translate([0, j, j-5]) 
                                        cube([2, 2, 2]);
                                }
                            }
                        }
                    }
                }
            }

            // Crown of Upper Egypt
            color("gold") {
                translate([-12, 0, 7]) {
                    difference() {
                        cylinder(h=3, d=6);
                        translate([-2, 0, -1]) cube([4, 6, 5]);
                    }
                }
            }

            // Scales pattern
            color("limegreen") {
                for(i = [5:5:body_length-5]) {
                    for(j = [0:60:360]) {
                        rotate([0, i*2, j]) {
                            translate([i, body_diameter/2, 0]) {
                                sphere(d=scale_size);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Render the creature
uadjet();

// Ba bird - human-headed falcon from ancient Egyptian mythology
// Represents the soul and personality, able to fly between worlds

// Overall dimensions: ~80mm wingspan, ~60mm height
$fa = 2;
$fs = 0.5;

// Main assembly
module ba_bird() {
    color("burlywood") {
        // Head - human-like with serene expression
        translate([0, 0, 35]) {
            union() {
                // Skull base
                sphere(d = 18);

