            }
        }

        // Right leg
        translate([5, 0, 8]) {
            rotate([10, 0, 0]) {
                leg();
            }
        }

        // Sacred solar disk crown
        color("gold") {
            translate([0, 0, 45]) {
                rotate_extrude() {
                    translate([8, 0]) circle(d = 2);
