            sphere(d=50);
            translate([-45,0,50]) 
            cube(50);
        }

        // Arms
        color("darkslategray") 
        union() {
            translate([10,-8,45]) 
            rotate([0,0,-20]) 
            cylinder(h=15, d=5);
            translate([-10,-8,45]) 
            rotate([0,0,20]) 
            cylinder(h=15, d=5);
            translate([10,-15,30]) 
            sphere(d=6);
            translate([-10,-15,30]) 
            sphere(d=6);
        }

        // Hips
        translate([0,0,20]) 
        cylinder(h=10, d=20);

        // Legs
        color("darkslategray") 
        union() {
            translate([7,0,10]) 
            cylinder(h=20, d=8);
            translate([-7,0,10]) 
            cylinder(h=20, d=8);

            // Taloned feet
            translate([7,-3,0]) 
            rotate([90,0,0]) 
            cylinder(h=12, d=8);
            translate([-7,-3,0]) 
            rotate([90,0,0]) 
            cylinder(h=12, d=8);

            // Toes
            for(i=[-4,0,4]) {
                translate([7+i,-7,0]) 
                rotate([90,0,0]) 
                cylinder(h=6, d=3);
                translate([-7+i,-7,0]) 
                rotate([90,0,0]) 
                cylinder(h=6, d=3);
            }
        }

        // Serpentine tail
        color("darkslateblue") 
        union() {
            translate([0,5,15]) 
            rotate([0,0,180]) 
            cylinder(h=30, d1=10, d2=5);
            translate([0,30,0]) 
            rotate([0,0,180]) 
            cylinder(h=25, d1=5, d2=3);
            translate([0,50,-10]) 
            rotate([0,0,180]) 
            cylinder(h=15, d1=3, d2=1);
        }

        // Scales pattern
        color("darkslategray") 
        for(z=[5:5:70]) 
            for(a=[0:30:360]) {
                rotate([0,0,a]) 
                translate([0,10,z]) 
                sphere(d=2);
            }
    }
}

