ardatlili();

// Maskim - Mesopotamian winged lion-demon guardian
// Realistic style, medium complexity, 3D printable

// Overall scale factor
scale_factor = 0.8;
main_color = "darkslateblue";
accent_color = "goldenrod";
eye_color = "red";

// Main body
module body() {
    color(main_color) {
        // Torso - lion body
        translate([0, 0, 5]) {
            difference() {
                scale([1.2, 0.8, 1]) sphere(d=20);
                translate([0, 0, -10]) cube([25, 25, 20], center=true);
            }
        }

        // Chest
        translate([0, 10, 0]) {
            scale([1.5, 1, 1.2]) sphere(d=15);
        }
    }
}

// Head with demonic features
module head() {
    color(main_color) {
        // Main head shape
        translate([0, 25, 15]) {
            scale([1, 1.2, 1.1]) sphere(d=18);
        }

        // Snout
        translate([0, 32, 15]) {
            scale([0.8, 0.6, 0.7]) sphere(d=12);
        }

        // Horns
        for (i = [-1, 1]) {
            translate([i * 8, 25, 25]) {
                rotate([0, i * -10, 0]) {
                    cylinder(h=12, d1=6, d2=2, center=false);
                }
            }
        }

        // Eyes
        color(eye_color) {
            for (i = [-1, 1]) {
                translate([i * 5, 30, 18]) {
                    sphere(d=4);
                }
            }
        }
    }
}

// Wings
module wings() {
    color(main_color) {
        for (side = [-1, 1]) {
            translate([side * 15, 0, 20]) {
                rotate([0, 0, side * 30]) {
                    // Upper wing
                    translate([0, 0, 0]) {
                        scale([1, 0.3, 0.1]) rotate([0, 90, 0]) 
                            cylinder(h=25, d1=20, d2=15);
                    }

                    // Lower wing section
                    translate([0, -5, -8]) {
                        scale([0.7, 0.2, 0.1]) rotate([0, 90, 0])
                            cylinder(h=18, d1=15, d2=10);
                    }

                    // Wing membrane support
                    for (i = [0:2]) {
                        translate([0, -i * 3, -i * 3]) {
                            rotate([30 * i, 0, 0]) {
                                scale([0.1, 0.1, 1]) 
                                    cylinder(h=15, d=1);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Legs
module legs() {
    color(main_color) {
        for (x = [-1, 1]) {
            // Front legs
            translate([x * 10, 20, 0]) {
                cylinder(h=15, d=6);
                // Paw
                translate([0, 0, -7.5]) {
                    sphere(d=7);
                }
            }

            // Back legs
            translate([x * 8, -10, 0]) {
                cylinder(h=15, d=6);
                translate([0, 0, -7.5]) {
                    sphere(d=7);
                }
            }
        }
    }
}

// Tail with scorpion stinger
module tail() {
    color(main_color) {
        translate([0, -20, 10]) {
            rotate([45, 0, 0]) {
                cylinder(h=15, d=4);
                // Scorpion stinger
                translate([0, -7, 7]) {
                    scale([0.5, 0.5, 1]) sphere(d=8);
                    translate([0, 0, 5]) {
                        cylinder(h=6, d1=4, d2=0);
                    }
                }
            }
        }
    }
}

// Decorative elements
module decorations() {
    color(accent_color) {
        // Chest amulet
        translate([0, 15, 8]) {
            scale([2, 1, 0.5]) sphere(d=5);
        }

        // Scale pattern on wings
        for (side = [-1, 1]) {
            for (i = [0:2]) {
                for (j = [0:1]) {
                    translate([side * (20 + i * 3), -j * 5 - 5, 20 - j * 8]) {
                        sphere(d=2);
                    }
                }
            }
        }
    }
}

// Assembly
scale(scale_factor) {
    union() {
        body();
        head();
        wings();
        legs();
        tail();
        decorations();
    }
}

