// Rock-inspired stool / side table
// Overall footprint ~120 mm diameter, 60 mm high
// 2 mm wall thickness for 3D printing

module rock_stool() {
    // Base plate (hidden, gives bottom a flat surface for printing)
    difference() {
        cylinder(h = 4, d = 120, $fn = 60);
        translate([0, 0, -1]) cylinder(h = 6, d = 118, $fn = 60);
    }

    // Central hollow cylinder for weight reduction and even walls
    difference() {
        cylinder(h = 56, d = 118, $fn = 60);
        translate([0, 0, 2]) cylinder(h = 52, d = 114, $fn = 60);
    }

    // Generate a ring of irregular rock lobes
    for (a = [0:30:360]) {
        rotate([0, 0, a]) {
            translate([rand(46, 52), 0, rand(12, 18)])
                scale([rand(0.8, 1.2), rand(0.8, 1.2), rand(0.8, 1.2)])
                    rock_lobe();
        }
    }

    // Top sitting surface
    difference() {
        cylinder(h = 4, d = 120, $fn = 60);
        translate([0, 0, 2]) cylinder(h = 2, d = 116, $fn = 60);
    }
}

// Single rock lobe primitive (organic, low-profile)
module rock_lobe() {
    // Main lobe
    difference() {
        union() {
            // Core lobe
            scale([1, 1, 0.6]) sphere(d = 18, $fn = 20);

            // Ridge detail
            rotate([90, 0, 0])
                translate([0, 0, -2])
                    scale([1, 0.3, 0.3])
                        cylinder(h = 12, d1 = 10, d2 = 6, $fn = 8);
        }

        // Small cavities for texture
        for (p = [[-3, 2, 2], [2, -2, 1.5], [-1, -3, 2.5]]) {
            translate(p)
                sphere(d = rand(3, 5), $fn = 8);
        }
    }
}

// Helper: pseudo-random in range
function rand(min, max) = min + (max - min) * (sin(123 * $fn) * 0.5 + 0.5);

rock_stool();

// Stone bench - realistic style, medium complexity
// Rough-hewn stone furniture piece with natural texture and structural supports

module stone_texture(x,y,z,freq,amp){
    // Creates organic rock surface texture with small bumps
    for(i=[0:freq:x]){
        for(j=[0:freq:y]){
            for(k=[0:freq:z]){
                translate([i,j,k]){
                    sphere(r=amp,$fn=6);
                }
            }
        }
    }
}

module stone_block(x,y,z,roughness){
    // Main stone block with irregular surface
    difference(){
        union(){
            // Base rectangular form
            cube([x,y,z], center=true);

            // Add surface texture
            translate([-x/2,-y/2,-z/2]){
                stone_texture(x,y,z,5,roughness);
            }
        }

        // Remove some material for natural wear
        for(i=[0:30]){
            rotate([rand(360),rand(360),rand(360)]){
                translate([rand(x)-x/2,rand(y)-y/2,rand(z)-z/2]){
                    sphere(r=rand(3)+1,$fn=8);
                }
            }
        }
    }
}

module bench_legs(){
    // Four supporting stone legs with taper
    leg_positions = [[-35, -15], [35, -15], [-35, 15], [35, 15]];

    for(pos = leg_positions){
        translate([pos[0], pos[1], -20]){
            difference(){
                // Tapered leg
                cylinder(h1=15, h2=12, r1=6, r2=8, $fn=8);

                // Carve natural indentations
                for(i=[0:4]){
                    rotate([0,0,i*90]){
                        translate([3,0,5]){
                            cube([8,3,10], center=true);
                        }
                    }
                }
            }
        }
    }
}

module seat_plank(){
    // Main seating surface with natural stone appearance
    translate([0,0,0]){
        difference(){
            union(){
                // Basic seat shape
                cube([80,40,8], center=true);

                // Front edge detail
                translate([0,20,0]){
                    cube([82,2,6], center=true);
                }

                // Back edge detail
                translate([0,-20,0]){
                    cube([82,2,6], center=true);
                }
            }

            // Carve seating depression
            translate([0,0,3]){
                cube([74,34,4], center=true);
            }

            // Drainage grooves
            for(i=[-30:10:30]){
                translate([i,0,4]){
                    cube([2,36,1], center=true);
                }
            }
        }
    }
}

module support_beam(){
    // Central support beam between legs
    translate([0,0,-10]){
        cube([70,15,5], center=true);
    }
}

// Main assembly
union(){
    color([0.5,0.5,0.5]){ // Stone gray

        // Seat
        seat_plank();

        // Leg structure
        bench_legs();

        // Support beam
        support_beam();

        // Side reinforcement blocks
        translate([-40,0,-15]){
            rotate([0,10,0]){
                stone_block(10,8,20,1.5);
            }
        }

        translate([40,0,-15]){
            rotate([0,-10,0]){
                stone_block(10,8,20,1.5);
            }
        }

        // Corner brackets
        corner_positions = [[-38, -18], [38, -18], [-38, 18], [38, 18]];
        for(pos = corner_positions){
            translate([pos[0], pos[1], -2]){
                difference(){
                    cube([6,6,6], center=true);
                    cylinder(h=8, r=2, $fn=8, center=true);
                }
            }
        }
    }
}

// Add natural weathering marks
for(i=[0:8]){
    color([0.3,0.3,0.3]){
        translate([rand(80)-40,rand(40)-20,rand(6)-3]){
            sphere(r=rand(2)+0.5,$fn=6);
        }
    }
}

// Pebble Bench - Realistic Style
// A smooth, organic bench resembling a large river pebble split in half
// Designed for 3D printing with 2mm wall thickness

$fn = 50;  // Smooth curves for printing

// Overall dimensions
bench_length = 120;
bench_width = 60;
bench_height = 45;

module pebble_bench() {
    // Create the main pebble shape
    difference() {
        // Outer smooth pebble shape
        scale([1, 0.7, 0.6])
        sphere(d = bench_length);

        // Hollow out the bottom for weight reduction
        translate([0, 0, -5])
        scale([0.9, 0.6, 0.4])
        sphere(d = bench_length - 4);
    }

    // Add the seat surface (flat top)
    difference() {
        // Main seat platform
        translate([0, 0, bench_height - 8])
        scale([1, 1, 0.3])
        sphere(d = bench_length * 0.9);

        // Create ergonomic depression
        translate([0, 0, bench_height - 4])
        scale([0.8, 0.7, 0.2])
        sphere(d = bench_length * 0.7);
    }

    // Structural support ribs inside
    for (i = [-bench_length/3, 0, bench_length/3]) {
        translate([i, 0, 5])
        rotate([0, 90, 0])
        cylinder(h = bench_width * 0.6, d = 4, $fn = 6);
    }

    // Add four legs with pebble-like feet
    leg_positions = [
        [-bench_length/3, -bench_width/3],
        [-bench_length/3, bench_width/3],
        [bench_length/3, -bench_width/3],
        [bench_length/3, bench_width/3]
    ];

    for (pos = leg_positions) {
        translate([pos[0], pos[1], 0])
        scale([1, 0.8, 0.6])
        sphere(d = 20);

        // Connect legs to bench with smooth transitions
        translate([pos[0], pos[1], 10])
        cylinder(h = 15, d1 = 20, d2 = 15);
    }

    // Add subtle surface texture
    for (i = [1:12]) {
        angle = i * 30;
        radius = bench_length/2 * 0.95;

        translate([
            cos(angle) * radius * 0.3,
            sin(angle) * radius * 0.3,
            bench_height - 6
        ])
        sphere(d = 3);
    }

    // Drainage holes for outdoor use
    for (i = [-bench_length/4, 0, bench_length/4]) {
        translate([i, 0, bench_height - 10])
        rotate([90, 0, 0])
        cylinder(h = 10, d = 4);
    }
}

// Assembly
pebble_bench();

// Add subtle edge beveling for printability
translate([0, 0, bench_height - 8])
scale([1.02, 1.02, 0.1])
sphere(d = bench_length * 0.85);

// Boulder Bench - Realistic style, medium complexity
// Overall dimensions: 100mm x 80mm x 50mm

$fn = 50;

// Main boulder body with organic shape
module boulder_body() {
    // Base elliptical form
    difference() {
        scale([1.2, 1, 0.8]) sphere(d = 90);

        // Carve out seating area
        translate([0, 0, 10]) {
            scale([0.9, 0.8, 0.6]) sphere(d = 90);
        }

        // Remove bottom for flat base
        translate([0, 0, -45]) cube([200, 200, 50], center = true);
    }
}

// Add surface irregularities for realism
module surface_details() {
    // Random bumps and dents
    for(i = [0:15:360]) {
        rotate([0, 0, i]) {
            translate([35 * sin(i), 25 * cos(i), 15]) {
                sphere(d = 5 + 3 * sin(i * 3));
            }
        }
    }

    // Small crevice details
    for(a = [30, 150, 270]) {
        rotate([0, 0, a]) {
            translate([20 * cos(a * 2), 15 * sin(a * 2), 5]) {
                difference() {
                    sphere(d = 8);
                    translate([0, 0, 5]) sphere(d = 7);
                }
            }
        }
    }
}

// Structural support frame inside
module support_frame() {
    // Central pillar
    translate([0, 0, -25]) {
        cylinder(h = 50, d = 20);
    }

    // Radial supports
    for(angle = [0:60:300]) {
        rotate([0, 0, angle]) {
            translate([15, 0, -25]) {
                rotate([0, 90, 0]) {
                    cylinder(h = 30, d = 8);
                }
            }
        }
    }
}

// Legs for furniture stability
module legs() {
    // Four legs at corners
    positions = [[-35, -25], [35, -25], [35, 25], [-35, 25]];

    for(pos = positions) {
        translate([pos[0], pos[1], -50]) {
            // Tapered leg
            cylinder(h1 = 25, h2 = 15, d1 = 12, d2 = 8);
        }
    }
}

// Assembly
union() {
    // Main boulder structure
    boulder_body();

    // Add surface texture
    surface_details();

    // Internal support
    support_frame();

    // Furniture legs
    legs();
}

// Crystal side table (50 mm side length, 75 mm height)
// Realistic style with 2 mm wall thickness for 3D printing

wall = 2;          // minimum wall thickness for printing
base = 50;         // table top edge length
height = 75;       // total height

// Table top â€“ hollow crystal plate with bevelled edges
module tabletop() {
    difference() {
        // outer crystal plate
        linear_extrude(height = wall * 1.5)
            offset(r = 2)
                offset(delta = -2)
                    square([base, base], center = true);

        // inner cavity for hollowing
        translate([0, 0, -0.1])
            linear_extrude(height = wall * 1.8)
                offset(r = 1)
                    offset(delta = -(wall + 2))
                        square([base - 8, base - 8], center = true);
    }
}

// Single crystal prism leg with slight taper
module leg() {
    hull() {
        // lower point
        translate([0, 0, 0])
            cylinder(h = 2, d1 = 2, d2 = 0.1, $fn = 6);
        // middle prism
        translate([0, 0, height * 0.5 - 5])
            cylinder(h = 10, d1 = 5, d2 = 4, $fn = 6);
        // upper connection
        translate([0, 0, height - 2])
            cylinder(h = 4, d1 = 4, d2 = 6, $fn = 6);
    }
}

// Reinforcing crystal strut between legs
module strut(len) {
    rotate([0, 90, 0])
        cylinder(h = len, d = 3, $fn = 6);
}

// Mounting frame under tabletop (hidden)
module frame() {
    difference() {
        // outer frame
        cube([base - 4, base - 4, wall * 2], center = true);
        // inner hollow
        cube([base - 8, base - 8, wall * 2.5], center = true);
    }
}

// Assemble the table
union() {
    // table top
    translate([0, 0, height])
        tabletop();

    // hidden frame under top
    translate([0, 0, height - wall])
        frame();

    // four crystal legs
    for (dx = [-1, 1])
        for (dy = [-1, 1])
            translate([dx * (base/2 - 6), dy * (base/2 - 6), 0])
                leg();

    // diagonal struts for stability
    strut_len = sqrt(pow(base - 12, 2) + pow(base - 12, 2));
    for (a = [0, 90, 180, 270])
        rotate([0, 0, a])
            translate([base/2 - 6, 0, height * 0.4])
                strut(strut_len - 8);
}

// Quartz coffee table with natural crystal top and metal frame
// Overall dimensions: 120mm x 80mm x 45mm

$fn = 32;

module quartz_crystal(x, y, z, size, rotation) {
    // Main crystal body
    translate([x, y, z])
    rotate([0, 0, rotation])
    scale([1, 1, 1.5])
    difference() {
        // Hexagonal prism base
        cylinder(h = size * 1.2, r = size/2, $fn = 6);

        // Faceted top
        translate([0, 0, size * 1.1])
        cylinder(h = size * 0.8, r1 = size/2, r2 = 0, $fn = 6);
    }

    // Side crystals
    for(i = [0:60:300]) {
        translate([x + cos(i) * size/3, y + sin(i) * size/3, z])
        rotate([0, 0, i + rotation])
        scale([0.3, 0.3, 0.8])
        cylinder(h = size * 0.6, r1 = size/4, r2 = 0, $fn = 6);
    }
}

module table_frame() {
    // Metal frame base
    difference() {
        // Main frame rectangle
        cube([120, 80, 3]);

        // Hollow center
        translate([2, 2, -1])
        cube([116, 76, 5]);
    }

    // Legs - cylindrical supports
    for(pos = [[10, 10], [110, 10], [10, 70], [110, 70]]) {
        translate([pos[0], pos[1], -42])
        cylinder(h = 42, r = 4);
    }

    // Cross supports
    translate([60, 5, -20])
    rotate([90, 0, 0])
    cylinder(h = 110, r = 2);

    translate([60, 75, -20])
    rotate([90, 0, 0])
    cylinder(h = 110, r = 2);

    translate([5, 40, -20])
    rotate([0, 90, 0])
    cylinder(h = 70, r = 2);

    translate([115, 40, -20])
    rotate([0, 90, 0])
    cylinder(h = 70, r = 2);
}

module quartz_cluster() {
    // Create cluster of crystals for table top
    for(i = [0:4]) {
        angle = i * 72;
        radius = 15 + (i % 2) * 10;
        quartz_crystal(
            cos(angle) * radius, 
            sin(angle) * radius, 
            3, 
            8 + (i % 3) * 3, 
            angle + 30
        );
    }

    // Central large crystal
    quartz_crystal(0, 0, 3, 12, 0);

    // Smaller accent crystals
    for(j = [0:8]) {
        angle = j * 40;
        radius = 25 + (j % 3) * 5;
        quartz_crystal(
            cos(angle) * radius * 0.7, 
            sin(angle) * radius * 0.7, 
            3, 
            4 + (j % 2) * 2, 
            angle + 15
        );
    }
}

// Assembly
union() {
    // Frame
    color("silver")
    table_frame();

    // Quartz top
    color([0.9, 0.9, 0.85])
    translate([0, 0, 3])
    quartz_cluster();
}

// Geode Side Table (Realistic Style)
// Dimensions: 120mm diameter, 350mm height
// Wall thickness: 2mm throughout

$fn = 50;

module geode_table() {
    // Base structure - hollow cone for stability
    difference() {
        cylinder(h=40, r1=50, r2=35, center=false);
        translate([0, 0, 2])
            cylinder(h=38, r1=48, r2=33, center=false);
    }

    // Central support column
    translate([0, 0, 40]) {
        difference() {
            cylinder(h=250, r=15, center=false);
            translate([0, 0, 2])
                cylinder(h=246, r=13, center=false);
        }
    }

    // Geode top - split sphere
    translate([0, 0, 290]) {
        // Outer shell - top half
        difference() {
            scale([1, 1, 0.6])
                sphere(r=60);
            translate([0, 0, -2])
                scale([0.97, 0.97, 0.6])
                    sphere(r=60);
        }

        // Crystalline interior
        translate([0, 0, -10]) {
            // Main crystal cluster
            for(i = [0:15:360]) {
                rotate([0, 0, i])
                    translate([random(5, 35), 0, 0])
                        rotate([random(-30, 30), random(-30, 30), 0])
                            crystal();
            }

            // Central large crystals
            translate([0, 0, 5])
                scale([1.5, 1.5, 2])
                    crystal();
            translate([20, 0, 10])
                rotate([0, 15, 0])
                    crystal();
            translate([-20, 0, 8])
                rotate([0, -15, 0])
                    crystal();
        }

        // Rim detail
        translate([0, 0, -12])
            difference() {
                cylinder(h=4, r=60, center=false);
                cylinder(h=4, r=58, center=false);
            }
    }

    // Support brackets
    for(i = [0:120:360]) {
        rotate([0, 0, i])
            translate([35, 0, 200])
                bracket();
    }
}

module crystal() {
    // Individual crystal formation
    hull() {
        translate([0, 0, 0])
            sphere(r=3);
        translate([0, 0, random(8, 15)])
            scale([0.3, 0.3, 1])
                sphere(r=2);
    }
}

module bracket() {
    // Triangular support bracket
    linear_extrude(height=8, center=false)
        polygon(points=[
            [0, 0],
            [20, 0],
            [10, 30]
        ]);
}

// Assembly
geode_table();

// Seashell-inspired accent stool
// Overall size: 120mm diameter x 180mm height

module shell_whirl(height, radius, thickness, steps) {
    // Build a spiral shell with hollow interior
    hull() {
        for (i = [0 : steps-1]) {
            angle = i * 360 / steps;
            current_height = height * (i / steps);
            current_radius = radius * (1 - i / (steps * 1.5));
            rotate([0, 0, angle])
                translate([current_radius, 0, current_height])
                    sphere(thickness, $fn=12);
        }
    }
}

module shell_base() {
    // Solid base for structural strength
    cylinder(h=15, r1=60, r2=50, $fn=48);
}

module seating_surface() {
    // Comfortable seating surface with shell texture
    difference() {
        cylinder(h=8, r1=55, r2=50, $fn=48);

        // Create scalloped edge pattern
        for(i=[0:11]) {
            rotate([0, 0, i*30])
                translate([45, 0, -1])
                    cube([15, 3, 10], center=true);
        }

        // Hollow center for weight reduction
        cylinder(h=10, r1=20, r2=15, $fn=32);
    }
}

module shell_ridges() {
    // Add spiral ridges for realistic shell appearance
    linear_extrude(height=2, twist=30)
        difference() {
            circle(55, $fn=48);
            circle(53, $fn=48);
        }
}

module seating_support() {
    // Central support column with spiral reinforcement
    difference() {
        cylinder(h=100, r1=25, r2=20, $fn=32);

        // Lightening holes for material savings
        for(i=[0:3]) {
            rotate([0, 0, i*90])
                translate([12, 0, 50])
                    cylinder(h=80, r=8, $fn=12);
        }
    }
}

module spiral_reinforcement() {
    // Add spiral ribs for strength and aesthetics
    for(i=[0:180:45]) {
        rotate([90, 0, i])
            translate([0, -25, 0])
                rotate_extrude(angle=360, $fn=24)
                    translate([25, 0])
                        polygon(points=[[0,0], [2,0], [2,100], [0,100]]);
    }
}

// Main assembly
union() {
    // Base structure
    shell_base();

    // Central support
    translate([0, 0, 15])
        seating_support();

    // Spiral reinforcement
    translate([0, 0, 15])
        spiral_reinforcement();

    // Seating surface
    translate([0, 0, 115])
        seating_surface();

    // Main shell structure
    translate([0, 0, 123])
        shell_whirl(height=55, radius=60, thickness=2, steps=20);

    // Add ridges to shell
    for(h=[0:10:50]) {
        translate([0, 0, 123+h])
            scale([1-h/100, 1-h/100, 1])
                shell_ridges();
    }

    // Final shell cap
    translate([0, 0, 178])
        scale([0.4, 0.4, 1])
            cylinder(h=2, r1=60, r2=0, $fn=48);
}

// Conch shell chair with realistic proportions and structural elements
// Overall dimensions: ~120mm height, ~100mm width, ~120mm depth

$fn = 32;  // Smooth curves for 3D printing

// Main shell body module
module shell_body() {
    // Base spiral structure
    difference() {
        // Main spiral shape
        union() {
            for (i = [0:15:360]) {
                rotate([0, 0, i]) 
                    translate([i*0.12, 0, i*0.08]) 
                        scale([1, 0.8, 1]) 
                            sphere(d = 35 - i*0.05);
            }
        }

        // Hollow interior
        translate([0, 0, -5])
            cylinder(h = 80, d1 = 25, d2 = 15, center = false);
    }
}

// Flared opening edge
module shell_lip() {
    difference() {
        // Outer lip
        rotate_extrude(angle = 180, $fn = 64)
            translate([25, 0])
                circle(r = 8);

        // Inner lip
        rotate_extrude(angle = 180, $fn = 64)
            translate([20, 0])
                circle(r = 6);
    }
}

// Structural ribs for strength
module support_ribs() {
    for (angle = [0:45:315]) {
        rotate([0, 0, angle]) {
            translate([0, 0, 10])
                linear_extrude(height = 60, twist = -30)
                    square([2, 30], center = true);
        }
    }
}

// Chair legs from shell base
module shell_legs() {
    // Front legs
    for (x = [-20:40:20]) {
        translate([x, 30, -15])
            cylinder(h = 40, d = 6, center = false);
    }

    // Rear support leg
    translate([0, -25, -15])
        cylinder(h = 40, d = 8, center = false);
}

// Natural texture ridges
module shell_ridges() {
    for (height = [10:5:70]) {
        translate([0, 0, height])
            rotate_extrude($fn = 32)
                translate([20 + height*0.2, 0])
                    circle(r = 1.5);
    }
}

// Seat surface
module seat_surface() {
    difference() {
        // Main seat
        translate([0, 0, 65])
            scale([1, 0.8, 0.3])
                sphere(d = 45);

        // Ergonomic contour
        translate([0, 0, 70])
            scale([0.6, 0.5, 0.2])
                sphere(d = 35);
    }
}

// Backrest curve
module backrest() {
    rotate([15, 0, 0])
        translate([0, -15, 70])
            scale([1, 0.6, 1.2])
                sphere(d = 35);
}

// Assembly
union() {
    // Main shell structure
    shell_body();

    // Structural additions
    translate([0, 0, 5])
        shell_lip();

    support_ribs();

    // Furniture elements
    seat_surface();
    backrest();

    // Legs
    shell_legs();

    // Decorative ridges
    shell_ridges();
}

// Base plate for stability
translate([0, 0, -15])
    cube([80, 70, 2], center = true);

// Scallop Shell Stool
// Realistic style, medium complexity
// Dimensions: ~120mm diameter, ~45mm height

$fn = 32;
wall_thickness = 2;
scale_factor = 1;

module scallop_shell(radius, height) {
    // Main shell body with scalloped edges
    difference() {
        // Base shell dome
        scale([1, 1, 0.4]) 
            sphere(r = radius);

        // Hollow interior
        translate([0, 0, -5])
            scale([0.85, 0.85, 0.5])
                sphere(r = radius - wall_thickness);
    }

    // Ridge details on shell surface
    for(i = [1:6]) {
        rotate([0, 0, i*30]) {
            hull() {
                translate([0, 0, height*0.3])
