    module branch(height, angle, rotation, thickness) {
        translate([0, 0, height])
        rotate([0, angle, rotation]) {
            // Main branch segment
            cylinder(h=40, d=thickness, $fn=8);

            // Branch joint reinforcement
            sphere(d=thickness+2, $fn=12);

            // Shelf platform
            translate([0, 0, 35])
                rotate([0, -angle, 0])
                cube([30, 20, 2], center=true);

            // Secondary branches for smaller shelves
            for(i = [1:3]) {
                rotate([0, 0, i*120])
                translate([thickness/2, 0, 15])
                rotate([0, 30, 0])
                    cylinder(h=25, d=thickness*0.7, $fn=6);
            }
        }
    }

    // Generate main branches at different heights and angles
    branch(20, 45, 0, 8);
    branch(45, -35, 90, 6);
    branch(70, 25, 180, 7);
    branch(95, -50, 270, 5);

    // Root-like support structures
    module root_support(x, y, angle) {
        translate([x, y, 0])
        rotate([0, 0, angle])
        linear_extrude(height=15, twist=30)
            circle(d=6, $fn=5);
    }

    // Add root supports
    for(i = [1:5]) {
        angle = i*72;
        x = cos(angle) * 12;
        y = sin(angle) * 12;
        root_support(x, y, angle);
    }

    // Coral texture details using small cylinders
    module coral_texture(base_x, base_y, base_z, count) {
        for(i = [1:count]) {
            translate([
                base_x + (rand(i*1) - 0.5) * 8,
                base_y + (rand(i*2) - 0.5) * 8,
                base_z + rand(i*3) * 10
            ])
                cylinder(h=2+rand(i*4)*3, d=1+rand(i*5), $fn=6);
        }
    }

    // Apply texture to main trunk
    for(j = [1:20]) {
        z = j * 6;
        angle = j * 137.5;
        x = cos(angle) * 10;
        y = sin(angle) * 10;
        coral_texture(x, y, z, 2);
    }

    // Shelf supports
    module shelf_support(x, y, z) {
        translate([x, y, z])
        rotate([0, 90, 0])
            cylinder(h=15, d=4, $fn=8);
    }

    // Add shelf supports between branches
    shelf_support(10, 0, 40);
    shelf_support(-10, 10, 65);
    shelf_support(0, -15, 90);

    // Top connector for structural integrity
    translate([0, 0, 120])
        sphere(d=10, $fn=12);
}

