    translate([x_pos, 0, 0]) {
        rotate([0, angle, 0]) {
            // Main branch cylinder
            cylinder(h=45, d=12, $fn=8);

            // Add knots and irregularities
            for(i = [0:10:40]) {
                translate([0, 0, i])
                    rotate([90, 0, 0])
                    cylinder(h=8, d1=14, d2=10, $fn=6);
            }

            // Root flare at base
            translate([0, 0, -2])
                cylinder(h=8, d1=18, d2=12, $fn=8);
        }
    }
}

// Support brace between legs
module support_brace() {
    translate([0, 0, 15]) {
        // Curved support beam
        hull() {
            translate([-40, 0, 0])
                rotate([0, 90, 0])
                cylinder(h=5, d=8, $fn=6);
            translate([40, 0, 0])
                rotate([0, 90, 0])
                cylinder(h=5, d=8, $fn=6);
        }

        // Add natural taper
        difference() {
            cube([85, 8, 5], center=true);
            translate([0, 0, -3])
                cube([87, 10, 3], center=true);
        }
    }
}

// Bark texture pattern
module bark_texture() {
    for(i = [-50:5:50]) {
        for(j = [-15:5:15]) {
            if((i+j)%2 == 0) {
                translate([i, j, 0.5])
                    cube([3, 1, 0.3]);
            }
        }
    }
}

// Assembly
union() {
    // Main seat
    difference() {
        seat_plank();
        // Hollow out for printing
        translate([0, 0, -1])
            scale([0.85, 0.85, 0.8])
            seat_plank();
    }

    // Apply bark texture to top
    translate([0, 0, 8.5])
        bark_texture();

    // Front legs
    branch_leg(-45, 0);
    branch_leg(45, 0);

    // Back support legs
    branch_leg(-35, -15);
    branch_leg(35, -15);

    // Cross support
    support_brace();

    // Add natural imperfections
    translate([-20, -15, 25])
        sphere(d=8, $fn=6);
    translate([25, 18, 30])
        sphere(d=6, $fn=5);
}

// Log side table â€“ realistic style, medium complexity
// Overall size ~110 mm high, 150 mm diameter top

$fa = 2; $fs = 0.5;

// Table top â€“ split log slice
module table_top(d=150, h=18) {
    difference() {
        cylinder(h=h, d=d, center=true);
        // Bark groove ring (2 mm deep)
        translate([0,0,-h/2+1])
            cylinder(h=2.2, d=d-8, center=true);

        // Pith hole
        translate([0,0,-h/2-0.1])
            cylinder(h=h+0.2, d=12, center=true);

        // Annual rings â€“ subtract thin cylinders
        for(i=[1:4:40]) {
            difference() {
                translate([0,0,-h/2-0.1])
                    cylinder(h=h+0.2, d=d-2*i, center=true);
                translate([0,0,-h/2-0.1])
                    cylinder(h=h+0.2, d=d-2*i-2, center=true);
            }
        }
    }
}

// Leg â€“ debarked log with slight taper
module leg(h=90, r=15) {
    union() {
        // Main cylinder
        cylinder(h=h, r1=r, r2=r*0.85, center=true);

        // Growth-ring lines
        for(z=[-h/2+5:10:h/2-5]) {
            translate([0,0,z])
                cylinder(h=0.6, r1=r+0.2, r2=r*0.85+0.2, center=true);
        }

        // Bottom flat pad for printing
        translate([0,0,-h/2])
            cylinder(h=2, r=r+1, center=false);
    }
}

// Simple mortise (negative) for joint on underside of top
module mortise() {
    rotate([0,90,0])
        cylinder(h=30, r=16, center=true);
}

// Assembly
difference() {
    union() {
        // Top
        translate([0,0,90/2+18/2])
            table_top();

        // Four legs
        for(a=[45:90:315]) {
            rotate([0,0,a])
                translate([0, 110/2-15, 0])
                    leg();
        }
    }

    // Cut mortises into top underside
    for(a=[45:90:315]) {
        rotate([0,0,a])
            translate([0, 110/2-15, 90/2-9])
                mortise();
    }
}

// Branch Shelf - Realistic Style
// A wall-mounted shelf made from a natural tree branch

// Parameters
branch_diameter = 10;           // mm
branch_length = 120;            // mm
shelf_thickness = 4;            // mm
wall_clearance = 15;            // mm
mount_hole_diameter = 5;        // mm
support_rod_diameter = 6;       // mm

// Main branch module
module branch_segment(length, diameter, curvature) {
    // Create curved branch using multiple cylinders
    segments = 8;
    for (i = [0:segments-1]) {
        angle = curvature * sin(i * 180 / segments);
        translate([i * length/segments, 0, sin(i * 180 / segments) * 2])
            rotate([0, angle, 0])
            cylinder(h = length/segments + 1, d = diameter, $fn = 12);
    }
}

// Bark texture detail
module bark_texture(diameter, length) {
    for (i = [0:20]) {
        rotate([0, 90, i * 18])
        translate([diameter/2 + 0.5, 0, i * length/20])
            cylinder(h = 0.5, d = 1.5, $fn = 6);
    }
}

// Wall mounting bracket
module wall_mount() {
    difference() {
        // Main mount block
        cube([20, 8, 15], center = true);

        // Mounting holes
        translate([7, 0, 0])
            rotate([90, 0, 0])
            cylinder(h = 10, d = mount_hole_diameter, $fn = 16);
        translate([-7, 0, 0])
            rotate([90, 0, 0])
            cylinder(h = 10, d = mount_hole_diameter, $fn = 16);
    }
}

// Shelf platform
module shelf_platform(width, depth) {
    difference() {
        // Main shelf
        translate([-width/2, 0, 0])
            cube([width, depth, shelf_thickness]);

        // Rounded edges
        translate([width/2 - 3, 3, -0.1])
            cylinder(h = shelf_thickness + 0.2, d = 6, $fn = 16);
        translate([width/2 - 3, depth - 3, -0.1])
            cylinder(h = shelf_thickness + 0.2, d = 6, $fn = 16);
    }
}

// Support rod
module support_rod(length) {
    cylinder(h = length, d = support_rod_diameter, $fn = 12);
}

// Assemble the branch shelf
union() {
    // Main branch
    translate([-branch_length/2, wall_clearance, branch_diameter/2 + shelf_thickness])
        branch_segment(branch_length, branch_diameter, 15);

    // Add bark texture
    translate([-branch_length/2, wall_clearance, branch_diameter/2 + shelf_thickness])
        bark_texture(branch_diameter, branch_length);

    // Wall mount
    translate([0, wall_clearance - 4, branch_diameter/2 + shelf_thickness + 7.5])
        wall_mount();

    // Shelf platform
    translate([0, wall_clearance + branch_diameter/2 + 2, branch_diameter/2 + shelf_thickness/2])
        shelf_platform(branch_length * 0.8, 40);

    // Support rods
    translate([-branch_length/3, wall_clearance + branch_diameter/2 + 2, shelf_thickness])
        rotate([90, 0, 0])
        support_rod(branch_diameter/2 + 2);

    translate([branch_length/3, wall_clearance + branch_diameter/2 + 2, shelf_thickness])
        rotate([90, 0, 0])
        support_rod(branch_diameter/2 + 2);

    // Small branch detail
    translate([branch_length/4, wall_clearance + 5, branch_diameter/2 + shelf_thickness + 8])
        rotate([0, 30, 90])
        branch_segment(25, branch_diameter * 0.6, -10);
}

// Twig Chair (realistic style, medium complexity)
// A small chair made from interlocking twigs, sized for 3D printing (â‰ˆ80 mm tall)

$fn = 32;        // Smooth curves for printing
wall = 2;        // 2 mm wall thickness for structural integrity

// Overall dimensions
chair_h = 80;
seat_w  = 60;
seat_d  = 50;

// Helper module: tapered cylinder that looks like a twig
module twig(h, r1, r2 = 0, detail = 8) {
    // Add subtle bumps for realism
    for(i = [0:detail-1]) {
        rotate([0, 0, i*360/detail])
            translate([0, 0, i*h/detail/2])
                cylinder(h = h/detail, r1 = r1*(1+0.05*sin(i*90)), r2 = r2*(1+0.05*sin(i*90)), center = true);
    }
}

// Helper module: twig with bark texture (small ridges)
module textured_twig(h, r1, r2 = 0) {
    difference() {
        twig(h, r1, r2);
        // Bark texture via small negative cylinders
        for(i = [0:h/2-1]) {
            rotate([0, 0, i*45])
                translate([0, 0, -h/2 + i*2])
                    cylinder(h = 1, r = r1*1.1, center = true);
        }
    }
}

// Seat frame made from bent twigs
module seat_frame() {
    // Front cross twig
    translate([0, seat_d/2-5, chair_h/2])
        rotate([90, 0, 0])
            textured_twig(seat_w, 3, 2.5);

    // Rear cross twig
    translate([0, -seat_d/2+5, chair_h/2])
        rotate([90, 0, 0])
            textured_twig(seat_w, 3, 2.5);

    // Side twigs
    translate([seat_w/2-5, 0, chair_h/2])
        rotate([90, 0, 90])
            textured_twig(seat_d-10, 3, 2.5);

    translate([-seat_w/2+5, 0, chair_h/2])
        rotate([90, 0, 90])
            textured_twig(seat_d-10, 3, 2.5);
}

// Backrest made from vertical twigs
module backrest() {
    for(x = [-seat_w/2+10:10:seat_w/2-10]) {
        translate([x, -seat_d/2+2, chair_h-15])
            rotate([0, 0, 0])
                textured_twig(30, 2.5, 2);
    }
}

// Legs made from thicker twigs
module legs() {
    // Front legs
    translate([seat_w/2-8, seat_d/2-8, chair_h/4])
        rotate([0, 0, 5])
            textured_twig(chair_h/2, 4, 3);

    translate([-seat_w/2+8, seat_d/2-8, chair_h/4])
        rotate([0, 0, -5])
            textured_twig(chair_h/2, 4, 3);

    // Rear legs (angled back)
    translate([seat_w/2-8, -seat_d/2+8, chair_h/4])
        rotate([10, 0, 5])
            textured_twig(chair_h/2+5, 4, 3);

    translate([-seat_w/2+8, -seat_d/2+8, chair_h/4])
        rotate([10, 0, -5])
            textured_twig(chair_h/2+5, 4, 3);
}

// Seat surface made from woven twigs
module seat_surface() {
    // Horizontal twigs
    for(y = [-seat_d/2+8:4:seat_d/2-8]) {
        translate([0, y, chair_h/2+2])
            rotate([90, 0, 0])
                twig(seat_w-10, 1.5, 1);
    }

    // Vertical twigs (woven)
    for(x = [-seat_w/2+8:4:seat_w/2-8]) {
        translate([x, 0, chair_h/2+3])
            rotate([90, 0, 90])
                twig(seat_d-10, 1.5, 1);
    }
}

// Assembly
union() {
    // Main structure
    seat_frame();
    backrest();
    legs();

    // Seat surface
    seat_surface();

    // Support twigs under seat
    translate([0, 0, chair_h/4])
        rotate([10, 0, 0])
            textured_twig(seat_w-5, 2.5, 2);

    translate([0, -seat_d/4, chair_h/3])
        rotate([0, 0, 90])
            rotate([10, 0, 0])
                textured_twig(seat_d-5, 2.5, 2);
}

// Add small branch joints for realism
module branch_joint(pos, rot, scale) {
    translate(pos)
        rotate(rot)
            scale([scale, scale, scale])
                sphere(5);
}

// Add joints at intersections
branch_joint([seat_w/2-8, seat_d/2-8, chair_h/2], [0, 0, 0], 0.6);
branch_joint([-seat_w/2+8, seat_d/2-8, chair_h/2], [0, 0, 0], 0.6);
branch_joint([seat_w/2-8, -seat_d/2+8, chair_h-15], [0, 0, 0], 0.6);
branch_joint([-seat_w/2+8, -seat_d/2+8, chair_h-15], [0, 0, 0], 0.6);

// Pinecone-inspired side table
// Overall height 120mm, diameter 60mm base

module pinecone_table() {
    // Table base - pinecone bottom half
    union() {
        // Central core cylinder for strength
        cylinder(h=80, d=16, $fn=32);

        // Pinecone scales in layers
        for(i = [0:8]) {
            // Each layer at different height
            translate([0, 0, i * 8]) {
                // Number of scales decreases toward top
                scale_count = max(12 - i, 6);

                for(j = [0:scale_count-1]) {
                    rotate([0, 0, j * (360/scale_count)]) {
                        // Pinecone scale shape
                        translate([8 + i * 1.5, 0, 0]) {
                            rotate([90, 0, 0]) {
                                scale([1, 1.2, 0.8]) {
                                    // Individual scale
                                    difference() {
                                        // Main scale body
                                        union() {
                                            cylinder(h=4, d1=10, d2=6, $fn=8);
                                            translate([0, 0, 2]) {
                                                sphere(d=8, $fn=16);
                                            }
                                        }
                                        // Hollow bottom for printing
                                        translate([0, 0, -1]) {
                                            cylinder(h=6, d=4, $fn=8);
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    // Table top - flat circular surface
    translate([0, 0, 85]) {
        difference() {
            // Main tabletop disk
            cylinder(h=6, d=60, $fn=64);

            // Light weight reduction pattern on underside
            for(x = [-20:20:20]) {
                for(y = [-20:20:20]) {
                    translate([x, y, -2]) {
                        cylinder(h=4, d=8, $fn=16);
                    }
                }
            }
        }

        // Decorative pinecone pattern on top
        for(i = [0:5]) {
            rotate([0, 0, i * 60]) {
                translate([20, 0, 6]) {
                    scale([0.3, 0.3, 0.2]) {
                        pinecone_table();
                    }
                }
            }
        }
    }

    // Hidden structural support rod
    translate([0, 0, -5]) {
        cylinder(h=10, d=12, $fn=32);
    }
}

// Generate the complete table
pinecone_table();

// Acorn Stool â€“ OpenSCAD
// Realistic style, medium complexity, 3D-printable

$fa = 2;
$fs = 0.5;

// Overall dimensions
cap_d = 90;
cap_h = 45;
stem_d = 30;
stem_h = 10;
body_d = 70;
body_h = 85;
leg_h = 220;
leg_r = 6;

module acorn_cap() {
    difference() {
        // Main cap dome
        scale([1, 1, 0.55])
            sphere(d = cap_d);

        // Remove bottom flat area
        translate([0, 0, -cap_h/2])
            cube([cap_d*2, cap_d*2, cap_h], center = true);

        // Add radial grooves for texture
        for (a = [0:30:330]) {
            rotate([0, 0, a])
                translate([cap_d/2 - 3, 0, -cap_h/2 + 2])
                    cube([cap_d, 2, cap_h], center = true);
        }
    }

    // Stem on top
    translate([0, 0, cap_h/2 - 1])
        cylinder(d = stem_d, h = stem_h, $fn = 20);
}

module acorn_body() {
    // Upper shoulder
    translate([0, 0, body_h/4])
        sphere(d = body_d);

    // Lower taper
    hull() {
        translate([0, 0, body_h/4])
            sphere(d = body_d);
        translate([0, 0, -body_h/2])
            sphere(d = body_d * 0.55);
    }

    // Light texture rings
    for (z = [-body_h/2 + 5:10:body_h/4 - 5]) {
        translate([0, 0, z])
            difference() {
                sphere(d = body_d + 0.4);
                sphere(d = body_d);
            }
    }
}

module leg() {
    // Simple round leg with 2 mm wall
    difference() {
        cylinder(h = leg_h, r = leg_r, $fn = 20);
        translate([0, 0, 2])
            cylinder(h = leg_h - 4, r = leg_r - 2, $fn = 20);
    }
}

module stool() {
    // Assemble cap and body
    union() {
        acorn_cap();
        translate([0, 0, cap_h/2 - 2])
            acorn_body();
    }

    // Legs
    leg_dist = 55;
    for (a = [45:90:315]) {
        rotate([0, 0, a])
            translate([leg_dist, 0, -leg_h/2 - body_h/2 + 5])
                leg();
    }
}

// Render
stool();

// Seed-shaped stool with walnut-like shell and internal structure
// Dimensions: 120mm height, 80mm max width, suitable for 3D printing

$fn = 32;  // Smooth curves for realistic appearance

module seed_stool() {
    // Main seed body - ellipsoid shape
    difference() {
        union() {
            // Outer shell - walnut-like shape
            scale([1, 0.7, 1.3]) 
                sphere(d=80);

            // Base flattening for stability
            translate([0, 0, -50])
                cube([80, 56, 20], center=true);
        }

        // Hollow interior for material efficiency
        scale([0.9, 0.6, 1.2])
            sphere(d=75);
    }

    // Internal support structure
    for (i = [-30:30:30]) {
        translate([i, 0, -30])
            rotate([0, 90, 0])
                cylinder(h=60, d=5, center=true);
    }

    for (j = [-20:20:20]) {
        translate([0, j, -20])
            rotate([90, 0, 0])
                cylinder(h=60, d=5, center=true);
    }

    // Seed seam detail - realistic walnut shell division
    for (angle = [0:120:240]) {
        rotate([0, 0, angle]) {
            translate([0, 0, -30])
                rotate([90, 0, 0])
                    cylinder(h=60, d=2);
            translate([0, 0, 30])
                rotate([90, 0, 0])
                    cylinder(h=60, d=2);
        }
    }

    // Top seating surface with slight indentation
    difference() {
        translate([0, 0, 40])
            scale([1, 0.7, 0.2])
                sphere(d=80);

        translate([0, 0, 45])
            scale([0.9, 0.6, 0.1])
                sphere(d=70);
    }

    // Structural legs disguised as root system
    module root_leg(x, y, angle) {
        translate([x, y, -50]) {
            rotate([angle, 0, 0]) {
                cylinder(h=40, d1=15, d2=8);
                translate([0, 0, -20])
                    sphere(d=8);
            }
        }
    }

    // Four root legs for stability
    root_leg(25, 20, 20);
    root_leg(-25, 20, 20);
    root_leg(25, -20, 20);
    root_leg(-25, -20, 20);

    // Surface texture - small bumps for realistic seed appearance
    for (i = [1:30]) {
        theta = i * 137.5;  // Golden angle for natural distribution
        phi = asin(-1 + 2 * i / 30);
        r = 40 - i * 0.5;

        translate([
            r * cos(theta) * cos(phi),
            r * sin(theta) * cos(phi) * 0.7,
            r * sin(phi) * 1.3
        ]) {
            sphere(d=2 + sin(i) * 0.5);
        }
    }

    // Reinforcement ring inside base
    difference() {
        translate([0, 0, -35])
            cylinder(h=5, d=70);

        translate([0, 0, -35])
            cylinder(h=5, d=60);
    }
}

// Generate the complete seed stool
seed_stool();

// Nutshell-inspired armchair â€“ realistic style, medium complexity
// Overall footprint â‰ˆ 100 mm across, printable without supports

$fa = 2; $fs = 0.5;  // smooth curves

module shell_half(h=40, r_base=45, thickness=2) {
    // create one half of a walnut-like shell
    difference() {
        // outer ellipsoid dome
        scale([1, 0.7, 0.8])
            sphere(r = r_base);

        // inner cavity
        translate([0, 0, -thickness])
            scale([1, 0.7, 0.75])
                sphere(r = r_base - thickness);

        // cut flat seating plane
        translate([0, 0, -h/2])
            cube([r_base*3, r_base*3, h], center = true);
    }
}

module ridges(count=12) {
    // decorative radial ridges on shell surface
    for(i=[0:count-1]) {
        rotate([0, 0, i*360/count])
            translate([0, 0, -5])
                linear_extrude(height = 10, twist = 5)
                    offset(delta = 1)
                        circle(d = 38, $fn = 3);
    }
}

module backrest() {
    // rear shell half acting as backrest
    rotate([0, 0, 0])
        shell_half();
}

module seat() {
    // front shell half, slightly rotated forward for seating angle
    translate([0, 0, 10])
        rotate([15, 0, 0])
            shell_half();
}

module legs() {
    // four short curved legs
    leg_positions = [[-30, -30], [30, -30], [-30, 30], [30, 30]];
    for(pos = leg_positions) {
        translate([pos[0], pos[1], -25])
            rotate([0, 0, 45])
                cylinder(h = 25, r1 = 4, r2 = 2, $fn = 8);
    }
}

module joint_pins() {
    // small pegs connecting shell halves
    pin_pos = [[0, 35, 5], [0, -35, 5]];
    for(p = pin_pos) {
        translate(p)
            rotate([90, 0, 0])
                cylinder(h = 8, d = 4, $fn = 8);
    }
}

module armrest_supports() {
    // side brackets shaped like nutshell edges
    translate([40, 0, 0])
        rotate([0, -20, 0])
            scale([1, 0.5, 0.5])
                shell_half(h = 20, r_base = 15);
    mirror([1, 0, 0])
        translate([40, 0, 0])
            rotate([0, -20, 0])
                scale([1, 0.5, 0.5])
                    shell_half(h = 20, r_base = 15);
}

// final assembly
union() {
    color("SaddleBrown") {
        backrest();
        seat();
        legs();
    }
    color("Tan") {
        ridges();
    }
    color("Sienna") {
        joint_pins();
        armrest_supports();
    }
}

