        cube([leg_thick, leg_thick, seat_height], center=true);
    translate([-seat_width/2 + leg_thick, -seat_depth/2 + leg_thick, seat_height/2])
        cube([leg_thick, leg_thick, seat_height], center=true);

    // Stretchers
    translate([0, 0, seat_height/4])
        cube([seat_width - leg_thick*2, leg_thick, leg_thick], center=true);
    translate([0, seat_depth/2 - leg_thick*1.5, seat_height/3])
        cube([seat_width - leg_thick*2, leg_thick, leg_thick], center=true);
}

// Armrests
module armrests() {
    // Left arm
    translate([-seat_width/2 - leg_thick/2, 0, seat_height + armrest_height/2])
        cube([leg_thick, seat_depth - 10, armrest_height - seat_height], center=true);

    // Right arm
    translate([seat_width/2 + leg_thick/2, 0, seat_height + armrest_height/2])
        cube([leg_thick, seat_depth - 10, armrest_height - seat_height], center=true);

    // Arm tops
    translate([-seat_width/2 - leg_thick/2, 0, armrest_height])
        cube([leg_thick + 5, seat_depth - 15, 4], center=true);
    translate([seat_width/2 + leg_thick/2, 0, armrest_height])
        cube([leg_thick + 5, seat_depth - 15, 4], center=true);
}

// Assembly
union() {
    seat_frame();
    backrest();
    legs();
    armrests();
}

// Walnut texture details (subtle)
for(i = [1:12]) {
    rotate([0, 0, i*30])
    translate([sin(i*30)*2, cos(i*30)*2, seat_height + 3])
        scale([1, 1, 0.1])
