}

// Coconut Stool - Realistic Style with Medium Complexity
// Dimensions: ~80mm height, ~70mm diameter

// Parameters
wall_thickness = 2;
coconut_radius = 35;
shell_thickness = 3;
leg_height = 45;
leg_radius = 4;
joint_radius = 6;

// Main coconut shell (hollow)
module coconut_shell() {
    difference() {
        // Outer shell
        sphere(r = coconut_radius, $fn = 64);

        // Hollow interior
        sphere(r = coconut_radius - shell_thickness, $fn = 64);

        // Flatten bottom for stability
        translate([0, 0, -coconut_radius + 5])
            cube([coconut_radius * 2, coconut_radius * 2, 10], center = true);

        // Drain holes (natural coconut features)
        for (i = [0:2]) {
            rotate([0, 0, i * 120])
                translate([coconut_radius * 0.6, 0, -coconut_radius + 10])
                    rotate([90, 0, 0])
                        cylinder(h = shell_thickness + 2, r = 3, $fn = 16);
        }
    }
}

// Coconut fiber texture pattern
module fiber_texture() {
    difference() {
        coconut_shell();

        // Fiber grooves
        for (i = [0:11]) {
            rotate([0, 0, i * 30])
                translate([0, 0, -coconut_radius])
                    cube([1.5, coconut_radius * 2, coconut_radius * 2], center = true);
        }

        // Horizontal rings
        for (h = [-20:5:20]) {
            translate([0, 0, h])
                rotate_extrude($fn = 64)
                    translate([coconut_radius, 0, 0])
                        circle(r = 0.5);
        }
    }
}

// Leg joint reinforcement
module leg_joint() {
    difference() {
        // Joint sphere
        sphere(r = joint_radius, $fn = 32);

        // Hollow center
        sphere(r = joint_radius - wall_thickness, $fn = 32);

        // Leg connection hole
        rotate([90, 0, 0])
            cylinder(h = joint_radius * 2, r = leg_radius, $fn = 16);
    }
}

// Structural leg
module leg() {
    difference() {
        // Main leg cylinder
        cylinder(h = leg_height, r = leg_radius, $fn = 16);

        // Hollow center for weight reduction
        cylinder(h = leg_height, r = leg_radius - wall_thickness, $fn = 16);

        // Tapered bottom
        translate([0, 0, leg_height - 5])
            cylinder(h = 5, r1 = leg_radius, r2 = leg_radius * 0.7, $fn = 16);
    }
}

// Assembly - Coconut stool with three legs
module coconut_stool() {
    union() {
        // Coconut seat with texture
        fiber_texture();

        // Three legs at 120 degree intervals
        for (i = [0:2]) {
            rotate([0, 0, i * 120]) {
                // Position joint under coconut
                translate([0, coconut_radius * 0.7, -coconut_radius + 5])
                    leg_joint();

                // Attach leg
                translate([0, coconut_radius * 0.7, -coconut_radius + 5 - leg_height/2])
                    rotate([90, 0, 0])
                        leg();
            }
        }

        // Reinforcement ring between legs
        translate([0, 0, -coconut_radius + 5 - leg_height * 0.7])
            difference() {
                cylinder(h = wall_thickness, r1 = coconut_radius * 0.8, r2 = coconut_radius * 0.75, $fn = 3);
                cylinder(h = wall_thickness + 1, r1 = coconut_radius * 0.8 - wall_thickness, 
                        r2 = coconut_radius * 0.75 - wall_thickness, $fn = 3);
            }
    }
}

// Render the stool
coconut_stool();

// Peanut Shell Chair - Realistic Style (Medium Complexity)
// 3D printable peanut shell inspired lounge chair
// Dimensions: 120mm x 100mm x 80mm

// Wall thickness for 3D printing
wall = 2;

// Main peanut shell body module
module peanut_shell_body() {
    // Outer shell - two intersecting ellipsoids
    translate([0, 0, 40]) {
        difference() {
            // First half of peanut
            scale([1.2, 0.8, 0.6]) sphere(50);

            // Carve out seating area
            translate([0, -10, -20]) {
                scale([1.1, 0.7, 0.5]) sphere(45);
            }

            // Bottom opening
            cube([200, 200, 80], center=true);
        }
    }

    // Second half of peanut
    translate([30, 0, 30]) {
        difference() {
            rotate([0, 0, 30]) scale([1.2, 0.8, 0.6]) sphere(45);

            // Seating cavity
            translate([0, -10, -15]) {
                scale([1.1, 0.7, 0.5]) sphere(40);
            }

            // Bottom opening
            cube([200, 200, 80], center=true);
        }
    }
}

// Structural support frame
module support_frame() {
    // Front support arch
    translate([-40, 0, 0]) {
        rotate([0, 90, 0]) {
            difference() {
                cylinder(h=80, r1=15, r2=15, center=true);
                cylinder(h=82, r1=13, r2=13, center=true);
            }
        }
    }

    // Rear support arch
    translate([40, 0, 10]) {
        rotate([0, 90, 0]) {
            difference() {
                cylinder(h=80, r1=15, r2=15, center=true);
                cylinder(h=82, r1=13, r2=13, center=true);
            }
        }
    }

    // Cross supports
    for(i = [-30, 0, 30]) {
        translate([i, 0, 5]) {
            difference() {
                cube([wall, 60, 10], center=true);
                translate([0, 0, -5]) cube([wall+0.5, 62, 12], center=true);
            }
        }
    }
}

// Surface texture details
module shell_texture() {
    // Ridge details along shell surface
    for(i = [0:15:180]) {
        rotate([0, i, 0]) {
            translate([10, 0, 40]) {
                scale([50, 0.5, 25]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=1, r1=1, r2=0.5);
                    }
                }
            }
        }
    }

    // Small surface bumps
    for(i = [0:20:200]) {
        for(j = [0:30:120]) {
            rotate([0, i, j]) {
                translate([45, 0, 40]) {
                    sphere(r=0.8);
                }
            }
        }
    }
}

// Leg support structure
module legs() {
    // Front legs
    translate([-30, -30, -40]) {
        difference() {
            cylinder(h=40, r1=4, r2=3);
            cylinder(h=41, r1=2, r2=1);
        }
    }

    translate([-30, 30, -40]) {
        difference() {
            cylinder(h=40, r1=4, r2=3);
            cylinder(h=41, r1=2, r2=1);
        }
    }

    // Rear legs
    translate([30, -30, -40]) {
        difference() {
            cylinder(h=40, r1=4, r2=3);
            cylinder(h=41, r1=2, r2=1);
        }
    }

    translate([30, 30, -40]) {
        difference() {
            cylinder(h=40, r1=4, r2=3);
            cylinder(h=41, r1=2, r2=1);
        }
    }
}

// Assembly
union() {
