// Gourd Stool
// Realistic style, medium complexity

// Dimensions (mm)
gourd_height = 90;
gourd_width = 70;
seat_thickness = 5;
leg_diameter = 8;
leg_height = 40;

// Wall thickness for 3D printing
wall_thickness = 2;

module gourd_shell() {
    // Main gourd body - upper bulb
    difference() {
        // Outer shell
        scale([1, 1.2, 1.1])
            sphere(d=gourd_width);

        // Hollow interior
        scale([0.9, 1.1, 0.95])
            sphere(d=gourd_width - 2*wall_thickness);
    }

    // Lower bulb
    translate([0, 0, -gourd_height/3])
        difference() {
            scale([0.8, 1, 0.9])
                sphere(d=gourd_width * 0.8);

            scale([0.7, 0.9, 0.8])
                sphere(d=gourd_width * 0.8 - 2*wall_thickness);
        }
}

module gourd_stem() {
    // Stem as center support
    translate([0, 0, gourd_height/2 + seat_thickness/2])
        cylinder(h=seat_thickness*2, d=20, $fn=32);
}

module seat_surface() {
    // Flat seating surface on top
    translate([0, 0, gourd_height/2 + seat_thickness/2])
        cylinder(h=seat_thickness, d=gourd_width * 0.9, $fn=64);
}

module legs() {
    // Four legs for stability
    leg_positions = [
        [gourd_width/4, gourd_width/4],
        [-gourd_width/4, gourd_width/4],
        [gourd_width/4, -gourd_width/4],
        [-gourd_width/4, -gourd_width/4]
    ];

    for(pos = leg_positions) {
        translate([pos[0], pos[1], -gourd_height/2 - leg_height/2])
            cylinder(h=leg_height, d=leg_diameter, $fn=16);
    }
}

module gourd_texture() {
    // Add subtle ridges for realistic texture
    for(i = [0:15:360]) {
        rotate([0, 0, i])
            translate([0, -gourd_width/2, 0])
                scale([1, 0.05, 1])
                    cylinder(h=gourd_height, d=wall_thickness);
    }
}

module joints() {
    // Reinforcement rings at connection points
    translate([0, 0, -gourd_height/2 - leg_height/2])
        cylinder(h=wall_thickness*2, d=gourd_width*1.1, $fn=32);
}

// Assembly
union() {
    gourd_shell();
    gourd_stem();
    seat_surface();
    legs();
    joints();
    gourd_texture();
}

// Pine Cone Stool â€“ 3D printable
// Overall height 90 mm, Ã˜ 120 mm, wall 2 mm
// Optimised for FDM â€“ no steep overhangs, 2 mm shells

$fn = 48;               // smooth curves

// ---------- PARAMETERS ----------
h_total   = 90;         // total stool height
r_top     = 60;         // seat radius
r_base    = 50;         // footprint radius
wall      = 2;          // shell thickness
scale_cnt = 8;          // number of scales per ring
rings     = 6;          // number of scale rings
core_h    = h_total*0.55;  // height of the hollow core
leg_h     = h_total-core_h; // leg length
leg_r     = 20;         // leg outer radius
leg_wall  = 2;          // leg wall thickness

module scale_ring(h, r_out, r_in, n){
    // single pine-cone scale
    module single_scale(){
        difference(){
            union(){
                // outer curved scale
                translate([-r_out/2,0,0])
                    scale([1,1,0.3])
                        sphere(r_out/2);
                // flat back
                translate([r_in/2-wall/2,0,0])
                    cube([wall, r_out*1.2, r_out*0.3], center=true);
            }
            // hollow out
            translate([0,0,-r_out])
                cube([r_out*2, r_out*2, r_out*2], center=true);
        }
    }

    // distribute around ring
    for(i=[0:n-1])
        rotate([0,0,i*360/n])
            translate([r_out*0.85,0,h])
                rotate([90,0,0])
                    single_scale();
}

module seat(){
    // hollow core
    difference(){
        cylinder(h=core_h, r1=r_base, r2=r_top);
        translate([0,0,-0.1])
            cylinder(h=core_h+0.2, r1=r_base-wall, r2=r_top-wall);
    }
    // scale rings
    for(i=[0:rings-1]){
        h = (i+0.5)*core_h/rings;
        r = r_base + (r_top-r_base)*(h/core_h);
        scale_ring(h, r, r-wall, scale_cnt);
    }
}

module leg(){
    // hollow cylinder with slight taper
    difference(){
        cylinder(h=leg_h, r1=leg_r, r2=leg_r-5);
        cylinder(h=leg_h+0.2, r1=leg_r-leg_wall, r2=leg_r-leg_wall-5);
    }
}

// ---------- ASSEMBLY ----------
union(){
    seat();
    translate([0,0,core_h])
        leg();
}
