    for(i=[0:spiral_turns*360]) {
        angle = i;
        radius = shell_diameter * 0.5 * (1 - i/(spiral_turns*360) * 0.8);
        height = i * (step_height/360);

        // Main spiral step
        translate([radius * cos(angle), radius * sin(angle), height])
            rotate([0, 0, angle])
            scale([1 - i/(spiral_turns*360) * 0.3, 0.6 - i/(spiral_turns*360) * 0.2, 1])
            cube([wall_thickness * 3, radius * 0.15, step_height * 1.2], center=true);
