// Main table top with burr-style interlocking pattern
module table_top_burr() {
    difference() {
        // Main top slab
        cube([table_top, table_top, wall_thickness * 3], center=true);

        // Burr pattern cuts
        for (i = [0:1]) {
            rotate([0, 0, i * 90]) {
                // Horizontal slots
                translate([0, -30, 0])
                    cube([80, 15, wall_thickness * 5], center=true);
                translate([0, 30, 0])
                    cube([80, 15, wall_thickness * 5], center=true);

                // Vertical slots
                translate([-30, 0, 0])
                    cube([15, 80, wall_thickness * 5], center=true);
                translate([30, 0, 0])
                    cube([15, 80, wall_thickness * 5], center=true);
            }
        }

        // Center burr joint
        translate([0, 0, 0])
            cube([20, 20, wall_thickness * 5], center=true);
    }
}

// Interlocking leg joint piece
module leg_joint(x, y) {
    translate([x, y, -table_height/2 + wall_thickness * 3]) {
        union() {
            // Vertical tenon
            cube([leg_thickness, leg_thickness, table_height/2 - wall_thickness * 3], center=true);

            // Horizontal mortise for cross support
            translate([0, 0, table_height/4]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cube([joint_size * 2, joint_size * 2, leg_thickness], center=true);
                        cube([joint_size, joint_size, leg_thickness + 2], center=true);
                    }
                }
            }
        }
    }
}

// Burr-style cross support
module cross_support() {
    translate([0, 0, -table_height/2 + wall_thickness * 3 + table_height/4]) {
        difference() {
            // Main support beam
            cube([table_top - leg_thickness * 2, joint_size * 1.5, joint_size * 1.5], center=true);

            // Mortise cuts for legs
            for (x = [-1, 1]) {
                for (y = [-1, 1]) {
                    translate([x * (table_top/2 - leg_thickness), y * (table_top/2 - leg_thickness), 0])
                        cube([joint_size, joint_size * 1.5 + 2, joint_size * 1.5 + 2], center=true);
                }
            }

            // Decorative burr pattern
            for (i = [0:3]) {
                rotate([0, 0, i * 90])
                    translate([25, 0, 0])
                        cube([40, joint_size * 1.5 + 2, joint_size], center=true);
            }
        }
    }
}

// Legs with burr-style joints
module legs() {
    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([x * (table_top/2 - leg_thickness/2), y * (table_top/2 - leg_thickness/2), 0]) {
                difference() {
                    // Main leg post
                    cube([leg_thickness, leg_thickness, table_height], center=true);

                    // Burr joint cutouts
                    translate([x * leg_thickness/2, 0, -table_height/4])
                        cube([wall_thickness * 3, leg_thickness - wall_thickness * 2, joint_size * 2], center=true);
                    translate([0, y * leg_thickness/2, -table_height/4])
                        cube([leg_thickness - wall_thickness * 2, wall_thickness * 3, joint_size * 2], center=true);
                }

                // Add leg joint
                leg_joint(x * (table_top/2 - leg_thickness/2), y * (table_top/2 - leg_thickness/2));
            }
        }
    }
}

// Assembly
