            scale([0.8 - i * 0.1, 1, 1])
            cylinder(h=leaf_thickness, r1=leaf_thickness, r2=length/2, $fn=3);

            // Right leaflet
            translate([0, -20 + i * 8, 0])
            rotate([0, 0, -angle])
            scale([0.8 - i * 0.1, 1, 1])
            cylinder(h=leaf_thickness, r1=leaf_thickness, r2=length/2, $fn=3);
