// Create honeycomb pattern
module honeycomb_pattern(radius, depth) {
    for (i = [-radius:cell_size*1.5:radius]) {
        for (j = [-radius:cell_size*1.5:radius]) {
            // Offset every other row
            x_offset = (j / (cell_size*1.5)) % 2 == 0 ? 0 : cell_size*0.866;

            // Check if within circular boundary
            if (sqrt(pow(i + x_offset, 2) + pow(j, 2)) < radius - cell_size) {
                // Hexagonal cell
                translate([i + x_offset, j, 0]) {
                    linear_extrude(height = depth) {
                        difference() {
                            circle(r = cell_size/2);
                            circle(r = cell_size/2 - wall);
                        }
                    }
                }
            }
        }
    }
}

// Structural base with legs
module base_structure() {
    // Main base ring
    difference() {
        cylinder(h = 8, r = diameter/2, center = true);
        cylinder(h = 10, r = diameter/2 - wall, center = true);
