// Create the main nest structure
module nest_shell() {
    difference() {
        // Outer shell with organic layers
        union() {
            for (i = [0:layers-1]) {
                translate([0, 0, i * layer_h]) {
                    // Scale each layer slightly for taper
                    scale([1 - 0.008 * i, 1 - 0.008 * i, 1]) {
                        // Organic wavy cylinder for each layer
                        cylinder(h = layer_h + 0.5, d = nest_d * (1 - 0.005 * i) + sin(i * 25) * 3, $fn = 64);
                    }
                }
            }
        }

        // Hollow out the center
        translate([0, 0, -1]) {
            cylinder(h = nest_h + 2, d = nest_d - 2 * wall_t, $fn = 64);
        }

        // Entrance hole at front
        translate([(nest_d - wall_t)/2 * 0.8, 0, base_h + 15]) {
            rotate([0, 90, 0]) {
                cylinder(h = wall_t + 2, d = 12, $fn = 32);
            }
        }
    }
}

// Create hexagonal cell pattern inside
module hex_cells() {
    cell_d = 3.5;
    rows = floor((nest_d - 2 * wall_t) / (cell_d * 1.5));

    difference() {
        // Thin shell for cell walls
        translate([0, 0, base_h + wall_t]) {
            cylinder(h = nest_h - base_h - 2 * wall_t, d = nest_d - 2 * wall_t, $fn = 6);
        }

        // Hexagonal holes
        for (r = [0:rows-1]) {
            for (c = [0:5]) {
                angle = c * 60 + (r % 2) * 30;
                radius = (r + 1) * cell_d * 0.75;

                rotate([0, 0, angle]) {
                    translate([radius, 0, base_h + wall_t + 1]) {
                        cylinder(h = nest_h - base_h - 2 * wall_t, d = cell_d, $fn = 6);
                    }
                }
            }
        }
    }
}

// Furniture base with legs
