    difference() {
        // Main base cylinder
        cylinder(h = base_h, d = nest_d, $fn = 64);

        // Hollow center for cable routing
        translate([0, 0, -1]) {
            cylinder(h = base_h + 2, d = 20, $fn = 32);
        }
    }

    // Three support legs
    for (i = [0:2]) {
        rotate([0, 0, i * 120]) {
            translate([nest_d/2 * 0.7, -5, 0]) {
                cube([20, 10, base_h]);
