    translate([nest_d/2 * 0.7, -5, 0]) {
        cube([15, 10, 5]);
    }
}

// Organic texture bumps on surface
module surface_texture() {
    for (i = [0:layers-1]) {
        for (j = [0:12]) {
            angle = j * 30 + i * 7;
            radius = (nest_d/2) * (1 - 0.005 * i) + sin(i * 25) * 1.5;

            rotate([0, 0, angle]) {
                translate([radius, 0, i * layer_h + layer_h/2]) {
                    sphere(d = 2 + sin(i * j) * 1, $fn = 8);
                }
            }
        }
    }
}

// Assembly
