    cylinder(h=web_height*0.7, r1=8, r2=5, center=false);

    // Web platform
    translate([0, 0, web_height*0.7 - 1]) {
        // Radial strands
        for(i = [0 : 360/radial_strands : 360-360/radial_strands]) {
            rotate([0, 0, i]) {
                linear_extrude(height=web_thickness, center=true) {
                    polygon(points=[
                        [0, 0],
                        [web_diameter*0.4, -1.5],
                        [web_diameter*0.4, 1.5]
                    ]);
                }
            }
        }

        // Concentric circles
        for(r = [web_diameter*0.2 : web_diameter*0.15 : web_diameter*0.45]) {
            rotate_extrude(angle=360, $fn=strand_segments) {
                translate([r, 0, 0]) {
                    square([web_thickness, web_thickness], center=true);
                }
            }
        }
    }

    // Outer web ring
    translate([0, 0, web_height*0.7 - 1]) {
        difference() {
            cylinder(h=web_thickness*2, r=web_diameter/2, center=true, $fn=48);
            cylinder(h=web_thickness*2.1, r=web_diameter/2 - web_thickness, center=true, $fn=48);
        }
    }

    // Legs
    for(i = [0 : 120 : 240]) {
        rotate([0, 0, i]) {
            translate([web_diameter*0.35, 0, 0]) {
                rotate([90, 0, 0]) {
                    cylinder(h=leg_height, r1=leg_radius, r2=leg_radius*0.7, center=false, $fn=8);
                }
            }
        }
    }

    // Leg base ring for stability
    translate([0, 0, -leg_height+5]) {
        difference() {
            cylinder(h=6, r=web_diameter*0.4, $fn=24);
            cylinder(h=6.1, r=web_diameter*0.4 - web_thickness, $fn=24);
        }
    }

    // Reinforcement struts between legs
    for(i = [0 : 120 : 240]) {
        rotate([0, 0, i]) {
            translate([web_diameter*0.35, 0, -leg_height/2 + 5]) {
                rotate([90, 0, 0]) {
                    cylinder(h=web_diameter*0.6, r=leg_radius*0.6, center=true, $fn=8);
                }
            }
        }
    }
}

// Web texture detail
module web_detail() {
    translate([0, 0, web_height*0.7]) {
        for(i = [0 : 30 : 330]) {
            rotate([0, 0, i]) {
                for(j = [web_diameter*0.25 : web_diameter*0.05 : web_diameter*0.45]) {
                    translate([j, 0, 0]) {
                        sphere(d=web_thickness*0.8, $fn=8);
                    }
                }
            }
        }
    }
}

// Add web details
web_detail();

// Spider Web Side Table
// Realistic style, medium complexity
// Dimensions: 120mm diameter x 45mm height
// Wall thickness: 2mm throughout for 3D printing

$fn = 64;  // Smooth curves for realistic appearance

// Main table top - circular with web pattern
module table_top() {
    // Solid top base - 2mm thick
    difference() {
        cylinder(h=2, r=60, center=false);

        // Web pattern grooves - 1mm deep
        for(i=[0:15:360]) {
            rotate([0, 0, i])
                translate([0, -60, 0])
                    cube([1, 120, 2], center=true);
        }

        // Concentric web rings
        for(r=[15:15:45]) {
            difference() {
                cylinder(h=2, r=r+0.5, center=false);
                cylinder(h=2, r=r-0.5, center=false);
            }
        }
    }
}

// Web strand legs - 8 legs like spider
module web_strand_leg(angle, radius) {
    // Main strand - 2mm diameter
    translate([0, 0, 0])
        rotate([90, 0, angle])
            cylinder(h=radius, r=1, center=true);

    // Support rings every 20mm
    for(h=[20:20:radius-10]) {
        translate([0, 0, h])
            rotate([90, 0, angle])
                cylinder(h=2, r=3, center=true);
    }
}

// Central support pillar - spider body inspired
module central_support() {
    // Main body - 20mm diameter, 40mm tall
    cylinder(h=40, r=10, center=false);

    // Segmented texture rings
    for(i=[1:5:38]) {
        translate([0, 0, i])
            difference() {
                cylinder(h=2, r=10, center=false);
                cylinder(h=2, r=8, center=false);
            }
    }

    // Base plate for stability
    translate([0, 0, -2])
        cylinder(h=2, r=15, center=false);
}

// Web anchor points - where strands meet legs
module anchor_point() {
    difference() {
        sphere(r=3);
        sphere(r=1);
    }
}

// Complete table assembly
union() {
    // Table top positioned at top
    translate([0, 0, 40])
        table_top();

    // Central support structure
    central_support();

    // 8 web strand legs at 45 degree intervals
    for(angle=[0:45:360]) {
        // Position anchors on table edge
        translate([50 * cos(angle), 50 * sin(angle), 40])
            anchor_point();

        // Web strands extending to floor
        translate([50 * cos(angle), 50 * sin(angle), 40])
            web_strand_leg(0, 40);

        // Floor anchors
        translate([50 * cos(angle), 50 * sin(angle), 0])
            anchor_point();
    }

    // Cross-webbing between legs for stability
    for(i=[0:45:360]) {
        j = (i + 45) % 360;
        hull() {
            translate([40 * cos(i), 40 * sin(i), 30])
                sphere(r=1);
            translate([40 * cos(j), 40 * sin(j), 30])
                sphere(r=1);
        }
    }
}

// Feather-inspired chair with realistic proportions
// Designed for 3D printing with 2mm walls and no overhangs

wall_thickness = 2;
feather_length = 120;
feather_width = 60;

// Main feather chair body
module feather_chair() {
    // Central spine
    union() {
        translate([0, 0, 5])
            scale([1, 0.3, 8])
                sphere(d=feather_length * 0.1);

        // Left side barbs
        for(i = [1:10:feather_length/2]) {
            translate([-i/2, feather_width/4, 5])
                rotate([0, 0, 10])
                    barb(i, feather_width/8 - i/10);
        }

        // Right side barbs
        for(i = [1:10:feather_length/2]) {
            translate([i/2, feather_width/4, 5])
                rotate([0, 0, -10])
                    barb(i, feather_width/8 - i/10);
        }

        // Seat base
        translate([0, 0, 0])
            cube([feather_length * 0.6, feather_width * 0.5, wall_thickness * 2], center=true);

        // Back support
        translate([0, -feather_width/4, feather_length * 0.2])
            rotate([100, 0, 0])
                cube([feather_length * 0.5, wall_thickness * 2, feather_length * 0.3], center=true);

        // Leg supports
        translate([-feather_length/4, 0, -10])
            cylinder(h=15, d=wall_thickness * 3);
        translate([feather_length/4, 0, -10])
            cylinder(h=15, d=wall_thickness * 3);
    }
}

// Individual barb structure
module barb(length, width) {
    hull() {
        translate([0, 0, 0])
            sphere(d=wall_thickness);
        translate([length/2, width, 0])
            sphere(d=wall_thickness);
    }
}

// Chair back with feather vein detail
module chair_back() {
    difference() {
        // Main back panel
        translate([0, -feather_width/2, feather_length/2])
            rotate([90, 0, 0])
                cube([feather_length * 0.7, feather_length * 0.5, wall_thickness * 2], center=true);

        // Vein cutouts
        for(i = [1:20:feather_length * 0.6]) {
            translate([i - feather_length/3, -feather_width/2, feather_length/2])
                rotate([90, 0, 0])
                    cube([2, feather_length * 0.4, wall_thickness * 3], center=true);
        }
    }
}

// Assembly
feather_chair();
chair_back();

