    translate([0, 0, chair_height/2])
        scale([rachis_width, chair_width*0.3, chair_height])
            cube(1, center=true);

    // Seat platform
    translate([0, 0, chair_height*0.3])
        scale([chair_length*0.6, chair_width*0.8, feather_thickness*2])
            cube(1, center=true);

    // Chair legs (barbs)
    for (i = [-3:3]) {
        // Front legs
        translate([i*15 + chair_length*0.3, 0, chair_height*0.15])
            rotate([90, 0, 0])
                cylinder(h=chair_height*0.7, r=barb_thickness*2, $fn=8);

        // Back legs
        translate([i*15 - chair_length*0.3, 0, chair_height*0.15])
            rotate([90, 0, 0])
                cylinder(h=chair_height*0.7, r=barb_thickness*2, $fn=8);
    }

    // Feather vanes (seat and back surfaces)
    for (i = [-5:5]) {
        // Left vane
        translate([i*10, -chair_width*0.35, chair_height*0.5 + i*5])
            rotate([0, 15, 0])
                scale([barb_length, barb_thickness, chair_height*0.3])
                    cube(1, center=true);

        // Right vane
        translate([i*10, chair_width*0.35, chair_height*0.5 + i*5])
            rotate([0, -15, 0])
                scale([barb_length, barb_thickness, chair_height*0.3])
                    cube(1, center=true);
    }

    // Structural cross supports between barbs
    for (i = [-2:2]) {
        translate([i*20, 0, chair_height*0.2])
            rotate([90, 0, 0])
                cylinder(h=chair_width*0.6, r=feather_thickness, $fn=6);
    }

    // Feather tip detail (chair back curve)
    difference() {
        translate([chair_length*0.45, 0, chair_height*0.7])
            scale([chair_length*0.2, chair_width*0.4, chair_height*0.4])
                sphere(r=1, $fn=20);

        // Hollow out for weight reduction
        translate([chair_length*0.45, 0, chair_height*0.7])
            scale([chair_length*0.15, chair_width*0.3, chair_height*0.3])
                sphere(r=1, $fn=20);
    }

    // Aftershaft detail (lower back support)
    translate([0, 0, chair_height*0.2])
        scale([chair_length*0.3, chair_width*0.2, feather_thickness*3])
            cube(1, center=true);

    // Calamus joint (connection point)
    translate([-chair_length*0.4, 0, feather_thickness])
        rotate([90, 0, 0])
            cylinder(h=feather_thickness*2, r1=rachis_width*1.5, r2=rachis_width, $fn=8);
}

