// Peacock Feather Chair Backrest
// Realistic style, medium complexity
// Designed for 3D printing with 2mm walls

$fn = 50;

// Overall dimensions
chair_width = 120;
chair_height = 150;
chair_depth = 20;

// Feather parameters
stem_diameter = 6;
eye_count = 5;
eye_spacing = 25;
feather_taper = 0.3;

module stem() {
    // Central stem
    translate([0, 0, chair_height/2])
        cylinder(h=chair_height, d1=stem_diameter, d2=stem_diameter*feather_taper);
}

module barbs() {
    // Individual barbs along stem
    for(i = [1:15]) {
        height = i * 8;
        barb_length = 20 + i * 1.5;
        for(side = [0, 1]) {
            mirror([side == 0 ? 0 : 1, 0, 0])
            translate([0, 0, chair_height - height])
            rotate([0, 90, 0])
            translate([0, 0, -barb_length/2])
            cube([barb_length, 1.5, 0.8], center=true);
        }
    }
}

module eye(eye_size, z_pos) {
    // Eye pattern with concentric circles
    color("gold")
    translate([0, 0, z_pos])
    difference() {
        // Outer circle
        cylinder(h=2, d=eye_size, center=true);

        // Inner circles
        translate([0, 0, 1])
        cylinder(h=2, d=eye_size*0.7, center=true);

        translate([0, 0, 2])
        color("teal")
        cylinder(h=2, d=eye_size*0.4, center=true);

        // Center pupil
        translate([0, 0, 3])
        color("navy")
        cylinder(h=2, d=eye_size*0.2, center=true);
    }
}

module eyes() {
    // Create multiple eyes along stem
    for(i = [0:eye_count-1]) {
        eye_size = 20 - i * 3;
        z_pos = chair_height - 30 - i * eye_spacing;
        eye(eye_size, z_pos);
    }
}

module support_frame() {
    // Structural frame for furniture
    frame_thickness = 3;

    // Top bar
    translate([0, chair_depth/2 - frame_thickness/2, chair_height - frame_thickness/2])
        cube([chair_width, frame_thickness, frame_thickness]);

    // Side bars
    for(side = [-1, 1]) {
        translate([side * chair_width/2, 0, chair_height/2])
            cube([frame_thickness, chair_depth, chair_height]);
    }

    // Bottom bar
    translate([0, chair_depth/2 - frame_thickness/2, frame_thickness/2])
        cube([chair_width, frame_thickness, frame_thickness]);
}

module mounting_holes() {
    // Holes for attaching to chair frame
    hole_diameter = 5;

    for(pos = [-40, 0, 40]) {
        translate([pos, chair_depth/2, 10])
            rotate([90, 0, 0])
            cylinder(h=10, d=hole_diameter);

        translate([pos, chair_depth/2, chair_height - 10])
            rotate([90, 0, 0])
            cylinder(h=10, d=hole_diameter);
    }
}

// Main assembly
difference() {
    union() {
        support_frame();
        stem();
        barbs();
        eyes();
    }
    mounting_holes();
}

