    translate([x, y, z]) {
        cylinder(h=h, r=3, $fn=6);
        translate([0, 0, h]) cylinder(h=2, r=2.5, $fn=6);
    }
}

module ice_leg(x, y, z) {
    // Creates a tapered ice leg with crystalline structure
    translate([x, y, z]) {
        // Main leg body
        difference() {
            cylinder(h=40, r1=6, r2=4);
            translate([0, 0, -1]) cylinder(h=42, r1=4, r2=2);
        }

        // Ice formations on leg
        for(i = [0:15:40]) {
            rotate([0, 0, i*73]) 
                ice_crystal(0, 4, i, 3 + sin(i)*2);
        }

        // Base support
        translate([0, 0, -2]) 
            cylinder(h=4, r=8);
    }
}

module bench_surface() {
    // Creates the main seating surface with ice-like undulations
    difference() {
        // Main bench shape
        translate([0, 0, 40]) 
            cube([120, 40, 5], center=true);

        // Hollow center for weight reduction
        translate([0, 0, 40]) 
            cube([116, 36, 5], center=true);

        // Surface texture - ice ridges
        for(x = [-55:10:55]) {
            for(y = [-15:8:15]) {
                translate([x, y, 42.5]) 
                    cube([8, 4, 1], center=true);
            }
        }
    }

    // Side ice formations
    for(side = [-1, 1]) {
        translate([side*55, 0, 42.5]) {
            difference() {
                cube([5, 40, 5], center=true);
                translate([0, 0, -1]) 
                    cube([1, 36, 7], center=true);
            }

            // Dripping ice details
            for(i = [-15:10:15]) {
                translate([0, i, 45]) 
                    sphere(r=2);
                translate([0, i, 47]) 
                    sphere(r=1.5);
            }
        }
    }
}

module support_beam() {
