    translate([0, 0, 20]) {
        difference() {
            cube([80, 5, 5], center=true);
            cube([76, 1, 1], center=true);
        }

        // Ice crystal decorations
        for(i = [-30:15:30]) {
            translate([i, 0, 22.5]) 
                rotate([90, 0, 0]) 
                    cylinder(h=3, r=2, $fn=6);
        }
    }
}

// Main assembly
