        // Frost crystal indentations on surface
        for (i = [-50:8:50]) {
            for (j = [-15:8:15]) {
                translate([i, j, 52]) {
                    rotate([0, 0, rand(i+j)*45]) {
                        scale([1.5, 1, 1]) {
                            cylinder(h=1, r=2, $fn=6);
                        }
                    }
                }
            }
        }
