// Snowflake Inspired Side Table
// Overall dimensions: ~120mm diameter, 45mm height
// 3D printable with 2mm wall thickness and no overhangs >45Â°

// Parameters
table_height = 45;
table_top_thickness = 4;
table_top_diameter = 120;
leg_thickness = 3;
leg_length = 40;
bracing_height = 15;

// Main table top with snowflake pattern
module table_top() {
    // Solid top
    difference() {
        cylinder(h=table_top_thickness, d=table_top_diameter, $fn=64);

        // Central hexagonal cutout
        translate([0, 0, -1])
            cylinder(h=table_top_thickness+2, d=20, $fn=6);

        // Six main arms cutouts (snowflake pattern)
        for(i=[0:5]) {
            rotate([0, 0, i*60]) {
                translate([0, 0, -1])
                    cube([leg_length*0.7, 8, table_top_thickness+2], center=true);

                // Side branches
                translate([20, 0, -1])
                    cube([15, 4, table_top_thickness+2], center=true);
                translate([-20, 0, -1])
                    cube([15, 4, table_top_thickness+2], center=true);
            }
        }
    }
}

// Single leg with hexagonal cross-section
module leg() {
    difference() {
        // Main hexagonal leg
        linear_extrude(height=leg_length, center=false) {
            circle(d=10, $fn=6);
        }

        // Hollow center for weight reduction
        translate([0, 0, 2])
            linear_extrude(height=leg_length-4, center=false) {
                circle(d=6, $fn=6);
            }
    }
}

// Bracing between legs (snowflake arms)
module bracing_arm() {
    difference() {
        cube([table_top_diameter*0.4, leg_thickness, bracing_height], center=true);

        // Decorative cutouts
        for(j=[-1, 1]) {
            translate([j*20, 0, bracing_height/2])
                cylinder(h=leg_thickness+2, d=8, $fn=6);
        }
    }
}

// Assembly
union() {
    // Table top
    translate([0, 0, leg_length])
        table_top();

    // Six legs in hexagonal pattern
    for(i=[0:5]) {
        rotate([0, 0, i*60])
            translate([35, 0, 0])
                leg();
    }

    // Six bracing arms connecting legs
    for(i=[0:5]) {
        rotate([0, 0, i*60])
            translate([0, 0, leg_length/2])
                bracing_arm();
    }

    // Central support hub
    difference() {
        cylinder(h=leg_length, d=20, $fn=12);
        translate([0, 0, 2])
            cylinder(h=leg_length-4, d=16, $fn=12);
    }
}

