// Stalactite side table
// Overall dimensions: Ã˜120 mm base, 220 mm tall

$fn = 72;               // smooth curves
wall = 2;               // 3D-printable wall thickness
base_d = 120;           // footprint on the floor
top_d = 80;             // smaller top plateau
h = 220;                // total height

module stalactite_core(r_top, r_bot, h, taper = 0.6) {
    // tapered, slightly wavy column
    slices = 20;
    for (i = [0 : slices-1]) {
        z0 = i * h / slices;
        z1 = (i+1) * h / slices;
        r0 = r_bot - (r_bot - r_top) * pow(z0 / h, taper) + 2 * sin(i * 25);
        r1 = r_bot - (r_bot - r_top) * pow(z1 / h, taper) + 2 * sin((i+1) * 25);
        translate([0, 0, z0])
            cylinder(h = h/slices + 0.1, r1 = r0, r2 = r1, $fn = 48);
    }
}

// outer shell (hollow)
difference() {
    stalactite_core(top_d/2, base_d/2, h);
    translate([0, 0, wall])
        stalactite_core(top_d/2 - wall, base_d/2 - wall, h - wall*2);
}

// tabletop plateau
translate([0, 0, h])
    difference() {
        cylinder(h = wall*2, r = top_d/2 + wall, $fn = 48);
        cylinder(h = wall*2 + 1, r = top_d/2 - wall, $fn = 48);
    }

// internal reinforcement rings every 40 mm
ring_interval = 40;
for (z = [ring_interval : ring_interval : h - ring_interval]) {
    r_mid = base_d/2 - (base_d/2 - top_d/2) * pow(z / h, 0.6);
    ring_r = r_mid - wall;
    translate([0, 0, z])
        difference() {
            cylinder(h = wall, r = ring_r + wall, $fn = 48);
            cylinder(h = wall + 1, r = ring_r, $fn = 48);
        }
}

// drip-tip detail at lower end
translate([0, 0, -wall])
    cylinder(h = wall*3, r1 = base_d/2, r2 = base_d/2 - wall, $fn = 48);

// subtle radial ribs for stiffness
ribs = 8;
for (a = [0 : 360/ribs : 359]) {
    rotate([0, 0, a])
        translate([base_d/2 - wall, 0, 0])
            cube([wall, wall*1.5, h], center = true);
}

// small drainage hole in tip (optional, easy to plug)
translate([0, 0, -wall*1.5])
    cylinder(h = wall*2, r = wall*1.5, $fn = 16);

// Stalagmite Side Table (medium complexity, realistic style)
// ~80 mm tall, 3D-printable, 2 mm wall thickness

// Overall dimensions
table_height = 80;
base_d = 60;
top_d = 40;

// Module for a tapered cylinder segment with organic noise
module tapered_segment(h, d1, d2, noise = 1) {
    // Create outer shell
    difference() {
        cylinder(h = h, d1 = d1, d2 = d2, $fn = 48);
        translate([0, 0, -0.1])
            cylinder(h = h + 0.2, d1 = d1 - 4, d2 = d2 - 4, $fn = 48);
    }
    // Add small bumps for stalagmite texture
    for (i = [0 : 15]) {
        angle = rands(0, 360, 1)[0];
        h_pos = rands(0, h, 1)[0];
        r = (d1 + (d2 - d1) * (h_pos / h)) / 2;
        translate([r * cos(angle), r * sin(angle), h_pos])
            sphere(d = noise * rands(1.5, 3, 1)[0], $fn = 8);
    }
}

// Module for structural rib reinforcement inside
module internal_rib(z, d_outer) {
    translate([0, 0, z])
        difference() {
            cylinder(h = 4, d = d_outer - 4, $fn = 32);
            cylinder(h = 4.2, d = d_outer - 8, $fn = 32);
        }
}

// Main stalagmite stem
module stalagmite_stem() {
    union() {
        tapered_segment(20, base_d, 50, 1.2);
        translate([0, 0, 20])
            tapered_segment(25, 50, 45, 1.0);
        translate([0, 0, 45])
            tapered_segment(20, 45, 35, 0.8);
        translate([0, 0, 65])
            tapered_segment(15, 35, top_d, 0.6);

        // Internal ribs for print stability
        internal_rib(10, 55);
        internal_rib(32.5, 47.5);
        internal_rib(55, 40);
    }
}

// Table top with shallow cavity
module table_top() {
    difference() {
        // Main disk
        cylinder(h = 5, d = top_d + 6, $fn = 64);
        // Recessed top surface
        translate([0, 0, 2])
            cylinder(h = 3, d = top_d, $fn = 64);
    }
}

// Small drip features on stem
module drip_features() {
    for (i = [1 : 3]) {
        angle = i * 120;
        h_pos = 10 + i * 20;
        r = (base_d + (top_d - base_d) * (h_pos / table_height)) / 2 - 2;
        translate([r * cos(angle), r * sin(angle), h_pos])
            rotate([90, 0, angle + 90])
                scale([1, 1, 0.4])
                    sphere(d = 6, $fn = 12);
    }
}

// Assembly
union() {
    stalagmite_stem();
    translate([0, 0, table_height - 5])
        table_top();
    drip_features();
}

