    rotate([0, 0, angle])
    translate([radius * cos(angle * 3), radius * sin(angle * 3), 0])
    rotate([0, 0, angle * 3])
    difference() {
        // Main segment body
        scale([1, 0.6, 1])
        cylinder(h=height, r1=thickness, r2=thickness*0.8, $fn=8);

        // Hollow center for 3D printing
        scale([0.8, 0.4, 1.2])
        cylinder(h=height+1, r1=thickness-2, r2=thickness*0.8-2, $fn=8);
    }
}

module fossil_top() {
    union() {
        // Central spiral structure
        for(i=[0:15:360]) {
            shell_segment(5 + i/15, 6, i, 3 + i/60);
        }

        // Outer growth rings as seating surface
        for(r=[30:5:48]) {
            difference() {
                cylinder(h=2, r=r, $fn=64);
                cylinder(h=3, r=r-2, $fn=64);
            }
        }

        // Ribs for structural support
        for(angle=[0:30:330]) {
            rotate([0, 0, angle])
            translate([0, 0, 2])
            linear_extrude(height=3)
            polygon(points=[[0,0],[40,2],[38,5],[-2,3]]);
        }
    }
}

