// Ammonite Side Table
// Realistic style, medium complexity
// Size: ~120mm diameter, 45mm height

$fn = 32;

// Main parameters
table_diameter = 120;
table_height = 45;
wall_thickness = 2;
segments = 8;

// Create the ammonite table
module ammonite_table() {
    // Base structure
    translate([0, 0, 0]) {
        // Central column support
        cylinder(h=table_height - 5, r1=15, r2=12, center=false);

        // Strengthening ring at base
        translate([0, 0, 0])
            cylinder(h=5, r1=20, r2=20, center=false);
    }

    // Ammonite spiral tabletop
    translate([0, 0, table_height - 5]) {
        spiral_top();
    }
}

// Generate the spiral tabletop
module spiral_top() {
    // Outer rim for structural integrity
    difference() {
        cylinder(h=5, r1=table_diameter/2, r2=table_diameter/2, center=false);

        // Hollow center
        cylinder(h=6, r1=table_diameter/2 - wall_thickness, r2=table_diameter/2 - wall_thickness, center=false);
    }

    // Create spiral chambers
    for(i = [1 : segments]) {
        angle = i * 45;
        radius = 10 + i * 7;

        // Chamber walls
        rotate([0, 0, angle])
        translate([radius, 0, 2.5])
            chamber_wall(i);
    }

    // Central spiral structure
    spiral_structure();
}

// Individual chamber walls
module chamber_wall(index) {
    wall_length = 8 + index * 1.5;
    wall_width = 2;
    wall_height = 5;

    translate([0, -wall_width/2, 0])
        cube([wall_length, wall_width, wall_height], center=false);
}

// Central spiral support structure
module spiral_structure() {
    turns = 3;
    points_per_turn = 20;
    start_radius = 5;
    end_radius = table_diameter/2 - 10;

    for(t = [0 : 1 : turns * points_per_turn]) {
        angle = t * 360 / points_per_turn;
        radius = start_radius + (end_radius - start_radius) * t / (turns * points_per_turn);

        translate([radius * cos(angle), radius * sin(angle), 2.5])
            sphere(r=1.5);
    }

    // Connect spheres with thin cylinders for printability
    for(t = [0 : 1 : turns * points_per_turn - 1]) {
        angle1 = t * 360 / points_per_turn;
        angle2 = (t + 1) * 360 / points_per_turn;
        radius1 = start_radius + (end_radius - start_radius) * t / (turns * points_per_turn);
        radius2 = start_radius + (end_radius - start_radius) * (t + 1) / (turns * points_per_turn);

        x1 = radius1 * cos(angle1);
        y1 = radius1 * sin(angle1);
        x2 = radius2 * cos(angle2);
        y2 = radius2 * sin(angle2);

        translate([(x1 + x2)/2, (y1 + y2)/2, 2.5])
            rotate([0, 0, atan2(y2 - y1, x2 - x1)])
                cylinder(h=sqrt(pow(x2 - x1, 2) + pow(y2 - y1, 2)), r=1, center=true);
    }
}

// Add decorative ridges on outer edge
module decorative_ridges() {
    for(i = [0 : 15 : 360]) {
        rotate([0, 0, i])
        translate([table_diameter/2 - 1, 0, 0])
            cube([2, 1, 5], center=false);
    }
}

// Main assembly
union() {
    ammonite_table();

    // Add decorative ridges
    translate([0, 0, table_height - 5])
        decorative_ridges();
}

// Trilobite Coffee Table (realistic style, medium complexity)
// Overall dimensions 120 x 80 x 45 mm

// Table top thickness
top_t = 6;

// Leg thickness
leg_t = 4;

// Body segments
segments = 11;

// Segment spacing
seg_space = 6;

// Segment width taper
seg_width = 30;

// Segment length
seg_length = 8;

// Head (cephalon) dimensions
head_w = 50;
head_l = 30;
head_h = 12;

// Thorax (body) dimensions
body_w = 40;
body_l = 70;
body_h = 10;

// Pygidium (tail) dimensions
tail_w = 35;
tail_l = 20;
tail_h = 8;

// Eye dimensions
eye_r = 8;
eye_l = 15;

// Genal spine length
genal_l = 12;

// Leg height
leg_h = 35;

// Joint radius
joint_r = 3;

// Table top
module table_top() {
    difference() {
        // Main top
        translate([0, 0, leg_h + top_t/2])
            cube([120, 80, top_t], center=true);

        // Trilobite outline cutout
        translate([0, 0, leg_h + top_t/2 - 0.1])
            linear_extrude(top_t + 0.2)
                trilobite_outline();
    }
}

// Trilobite outline for table top
module trilobite_outline() {
    union() {
        // Head
        translate([-body_l/2 - head_l/2, 0, 0])
            scale([head_l, head_w, 1])
                circle(d=1);

        // Body segments
        for(i = [0:segments-1]) {
            translate([-body_l/2 + i*seg_space, 0, 0])
                scale([seg_length, seg_width * (1 - i*0.05), 1])
                    circle(d=1);
        }

        // Tail
        translate([body_l/2 + tail_l/2, 0, 0])
            scale([tail_l, tail_w, 1])
                circle(d=1);
    }
}

// Trilobite body structure
module trilobite_body() {
    union() {
        // Head
        translate([-body_l/2 - head_l/2, 0, leg_h - head_h/2])
            scale([head_l, head_w, head_h])
                sphere(d=1);

        // Eyes
        translate([-body_l/2 - head_l/2 + eye_l/2, head_w/2 - eye_r, leg_h - head_h/2 + eye_r])
            rotate([0, 90, 0])
                cylinder(h=eye_l, r=eye_r);

        translate([-body_l/2 - head_l/2 + eye_l/2, -head_w/2 + eye_r, leg_h - head_h/2 + eye_r])
            rotate([0, 90, 0])
                cylinder(h=eye_l, r=eye_r);

        // Genal spines
        translate([-body_l/2 - head_l/2 - genal_l/2, head_w/2, leg_h - head_h/2])
            scale([genal_l, 4, head_h])
                sphere(d=1);

        translate([-body_l/2 - head_l/2 - genal_l/2, -head_w/2, leg_h - head_h/2])
            scale([genal_l, 4, head_h])
                sphere(d=1);

        // Body segments
        for(i = [0:segments-1]) {
            translate([-body_l/2 + i*seg_space, 0, leg_h - body_h/2])
                scale([seg_length, seg_width * (1 - i*0.05), body_h])
                    sphere(d=1);
        }

        // Tail
        translate([body_l/2 + tail_l/2, 0, leg_h - tail_h/2])
            scale([tail_l, tail_w, tail_h])
                sphere(d=1);
    }
}

// Support legs
module legs() {
    // Front legs
    translate([-40, 30, leg_h/2])
        cylinder(h=leg_h, r=leg_t, $fn=6);

    translate([-40, -30, leg_h/2])
        cylinder(h=leg_h, r=leg_t, $fn=6);

    // Middle legs
    translate([0, 35, leg_h/2])
        cylinder(h=leg_h, r=leg_t, $fn=6);

