// Amber Coffee Table
// Realistic style, medium complexity

// Overall dimensions
table_length = 140;
table_width = 70;
table_height = 45;
amber_thickness = 8;

// Leg parameters
leg_width = 6;
leg_height = 40;
leg_inset = 8;

// Create organic amber top with natural inclusions
module amber_top() {
    difference() {
        // Main amber slab with rounded edges
        translate([0, 0, amber_thickness/2])
        minkowski() {
            cube([table_length, table_width, amber_thickness], center=true);
            sphere(3);
        }

        // Natural cracks and inclusions
        for(i=[1:5]) {
            rotate([0, 0, i*35])
            translate([
                sin(i*50)*table_length/3, 
                cos(i*30)*table_width/3, 
                0
            ])
            scale([1, 0.4, 1])
            cylinder(h=amber_thickness+2, r1=3, r2=1, center=true);
        }

        // Surface imperfections
        for(j=[1:8]) {
            translate([
                (j*17)%table_length - table_length/2,
                (j*23)%table_width - table_width/2,
                amber_thickness/2 - 1
            ])
            sphere(1.5);
        }
    }
}

// Wooden frame base with amber inlays
module frame_base() {
    difference() {
        // Main frame
        cube([table_length, table_width, 4], center=true);

        // Center cutout
        cube([table_length-leg_inset*2, table_width-leg_inset*2, 5], center=true);
    }
}

// Decorative amber inlays in frame
module amber_inlays() {
    for(x=[-table_length/2+15 : 30 : table_length/2-15]) {
        for(y=[-table_width/2+15 : 30 : table_width/2-15]) {
            translate([x, y, 2])
            scale([1, 0.6, 1])
            cylinder(h=3, r=4, center=true);
        }
    }
}

// Leg assembly with joints
module leg(x_pos, y_pos) {
    // Main leg
    translate([x_pos, y_pos, leg_height/2+4])
    cube([leg_width, leg_width, leg_height], center=true);

    // Joint bracket
    translate([x_pos, y_pos, 4])
    difference() {
        cube([leg_width+6, leg_width+6, 4], center=true);
        translate([0, 0, -1])
        cube([leg_width, leg_width, 6], center=true);
    }

    // Amber accent on leg
    translate([x_pos, y_pos, leg_height/2+2])
    rotate([90, 0, 0])
    cylinder(h=leg_width+2, r1=3, r2=3, center=true);
}

// Assembly
union() {
    // Amber top
    amber_top();

    // Frame base
    translate([0, 0, amber_thickness+2])
    frame_base();

    // Amber inlays in frame
    translate([0, 0, amber_thickness+3])
    amber_inlays();

    // Four legs
    leg(table_length/2-leg_inset, table_width/2-leg_inset);
    leg(-table_length/2+leg_inset, table_width/2-leg_inset);
    leg(table_length/2-leg_inset, -table_width/2+leg_inset);
    leg(-table_length/2+leg_inset, -table_width/2+leg_inset);
}

