    difference() {
        // Outer seat shell
        union() {
            // Seat top curved surface
            translate([0, 0, 45]) {
                scale([1, 1.2, 0.3]) {
                    sphere(d=70, $fn=50);
                }
            }
            // Seat edge rim
            translate([0, 0, 42]) {
                cylinder(h=5, d1=72, d2=75, $fn=50);
            }
        }

        // Hollow out seat bottom
        translate([0, 0, 40]) {
            scale([0.9, 1.1, 0.2]) {
                sphere(d=65, $fn=50);
            }
        }
    }

    // Backrest - flowing organic shape
    union() {
        // Backrest support posts (2)
        for(x = [-25, 25]) {
            translate([x, 0, 85]) {
                rotate([0, 10, 0]) {
                    cylinder(h=50, d1=8, d2=10, $fn=20);
                }
            }
        }

        // Backrest panel
        translate([0, 0, 110]) {
            scale([1.2, 0.8, 0.15]) {
                sphere(d=70, $fn=50);
            }
        }
    }

    // Legs - tapered resin columns
    module leg() {
        translate([0, 0, 20]) {
            cylinder(h=40, d1=12, d2=8, $fn=20);
        }
    }

    // Position 4 legs
    positions = [[-30, -30], [30, -30], [30, 30], [-30, 30]];
    for(pos = positions) {
        translate([pos[0], pos[1], 0]) {
            leg();
        }
    }

    // Support beams between legs
    module beam(length) {
        translate([0, 0, 15]) {
            rotate([90, 0, 0]) {
                cylinder(h=length, d=6, $fn=20);
            }
        }
    }

    // Front and back beams
    translate([-30, 0, 0]) {
        beam(60);
    }

    // Side beams
    rotate([0, 0, 90]) {
        translate([-30, 0, 0]) {
            beam(60);
        }
    }

    // Armrests - curved resin blocks
    module armrest(side) {
