wall_thickness = 2;

// Main module
module ball_ottoman() {
    // Base structure (hidden support ring)
    difference() {
        cylinder(h=base_thickness, d=ball_diameter*0.9, $fn=100);
        translate([0, 0, -0.1])
            cylinder(h=base_thickness+0.2, d=ball_diameter*0.9-wall_thickness*2, $fn=100);
    }

    // Legs
    for (i = [0:3]) {
        rotate([0, 0, i*90])
            translate([ball_diameter*0.35, 0, base_thickness])
                leg();
    }

    // Main ball cushion
    translate([0, 0, base_thickness+leg_height])
        difference() {
            sphere(d=ball_diameter, $fn=100);

            // Hollow interior
            sphere(d=ball_diameter-wall_thickness*2, $fn=100);

            // Flat bottom
            translate([0, 0, -ball_diameter/2])
                cube([ball_diameter, ball_diameter, ball_diameter], center=true);
        }

    // Cushion top surface
    translate([0, 0, base_thickness+leg_height+ball_diameter/2-cushion_thickness])
        cushion_surface();

    // Buttons for tufting
    for (i = [0:1]) {
        for (j = [0:1]) {
            translate([
                (i-0.5)*ball_diameter*0.3,
                (j-0.5)*ball_diameter*0.3,
                base_thickness+leg_height+ball_diameter/2-button_height
            ])
                button();
        }
    }
}

