module leg() {
    difference() {
        cylinder(h=leg_height, d=leg_diameter, $fn=32);
        translate([0, 0, -0.1])
            cylinder(h=leg_height+0.2, d=leg_diameter-wall_thickness*2, $fn=32);
    }

    // Leg foot detail
    translate([0, 0, -0.5])
        cylinder(h=3, d=leg_diameter*1.2, $fn=32);
}

// Cushion surface with stitching
module cushion_surface() {
    difference() {
        // Main cushion
        sphere(d=ball_diameter-wall_thickness*2, $fn=100);

        // Stitching grooves
        for (i = [0:3]) {
            rotate([0, 0, i*90])
                translate([0, -ball_diameter/2+wall_thickness, 0])
                    stitch_line();
        }

        // Diagonal stitch lines
        for (i = [0:1]) {
            rotate([0, 0, 45+i*90])
                translate([0, -ball_diameter/2+wall_thickness, 0])
                    stitch_line();
        }
    }
}

// Stitching detail
module stitch_line() {
    for (i = [-3:3]) {
        translate([i*stitch_spacing, 0, 0])
            cube([stitch_width, ball_diameter, stitch_depth], center=true);
    }
}

// Tufting button
module button() {
    difference() {
        cylinder(h=button_height, d=button_diameter, $fn=32);
        translate([0, 0, -0.1])
            cylinder(h=button_height+0.2, d=button_diameter-wall_thickness*2, $fn=32);
    }

    // Button top detail
    translate([0, 0, button_height])
        sphere(d=button_diameter*0.9, $fn=32);
}

