        // Hollow interior
        sphere(d = ball_diameter - wall_thickness*2, $fn = 64);

        // Flat seating surface on top
        translate([0, 0, ball_diameter/2 - 2])
            cube([ball_diameter*0.8, ball_diameter*0.8, 10], center = true);
    }
}

// Volleyball panel seams
module volleyball_seams() {
    // Generate 6 great circles for volleyball pattern
    for(i = [0:5]) {
        rotate([0, 0, i*60])
        rotate([90, 0, 0])
        translate([0, 0, -ball_diameter/2])
        linear_extrude(height = ball_diameter, center = true)
        circle(d = ball_diameter, $fn = 100);
    }

    // Additional perpendicular seam
    rotate([90, 0, 0])
    translate([0, 0, -ball_diameter/2])
    linear_extrude(height = ball_diameter, center = true)
    circle(d = ball_diameter, $fn = 100);
}

// Structural legs with tapered design
module stool_legs() {
    leg_radius = 6;
    base_radius = 8;

    for(i = [0:2]) {
        angle = i*120;

        // Leg from ball to floor
        translate([cos(angle)*(ball_diameter/2 - 5), sin(angle)*(ball_diameter/2 - 5), -leg_height/2])
        rotate([0, 0, angle])
        rotate([90, 0, 0])
        cylinder(h1 = base_radius, h2 = leg_radius, h = leg_height, $fn = 20);

        // Reinforcement gusset
        translate([cos(angle)*(ball_diameter/2 - 5)*0.7, sin(angle)*(ball_diameter/2 - 5)*0.7, -leg_height/3])
        cube([wall_thickness*2, wall_thickness*2, leg_height/3], center = true);
    }
}

// Floor base ring for stability
module base_ring() {
    difference() {
        // Outer ring
        cylinder(h = 8, r1 = ball_diameter*0.6, r2 = ball_diameter*0.55, $fn = 32);

        // Inner hollow
        cylinder(h = 10, r = ball_diameter*0.45, $fn = 32);
    }
}

// Anti-slip foot pads
module foot_pads() {
    pad_radius = 10;
    pad_height = 3;

    for(i = [0:2]) {
        angle = i*120;
        translate([cos(angle)*(ball_diameter*0.5), sin(angle)*(ball_diameter*0.5), -leg_height - pad_height/2])
        cylinder(h = pad_height, r = pad_radius, $fn = 16);
    }
}

