    translate([0, 0, -leg_height - 4])
    base_ring();

    // Foot pads
    foot_pads();
}

// Golfball Stool - Realistic style, medium complexity
// Dimensions: 120mm diameter golfball, 300mm total height

$fn = 64;

// Main parameters
ball_diameter = 120;
stool_height = 300;
leg_thickness = 20;
base_diameter = 100;

// Golf ball dimple pattern parameters
dimple_radius = 3;
dimple_depth = 1.5;
dimple_rows = 8;

module dimple_pattern() {
    // Create dimples on surface using spherical holes
    for (i = [0:dimple_rows-1]) {
        angle = i * 180 / (dimple_rows - 1);
        row_radius = (ball_diameter/2) * sin(angle);
        dimples_in_row = floor(2 * PI * row_radius / (dimple_radius * 2.5));

        for (j = [0:dimples_in_row-1]) {
            rotate_angle = j * 360 / dimples_in_row;

            rotate([0, angle, rotate_angle])
                translate([0, 0, ball_diameter/2])
                    rotate([0, 180, 0])
                        sphere(dimple_radius);
        }
    }
}

module golfball_surface() {
    difference() {
        sphere(d = ball_diameter);
        dimple_pattern();
    }
}

module leg_support() {
    // Central support column
    cylinder(h = stool_height - ball_diameter/2, d = 30);

    // Reinforcement rings
    for (i = [1:3]) {
        translate([0, 0, i * (stool_height - ball_diameter/2) / 4])
            cylinder(h = 2, d = 35);
    }
}

module base() {
    // Wide base for stability
    cylinder(h = 20, d = base_diameter);

    // Anti-slip ring
    translate([0, 0, 18])
        cylinder(h = 2, d = base_diameter + 10);
}

module structural_joints() {
    // Top joint - connects ball to support
    translate([0, 0, stool_height - ball_diameter/2 - 5])
        cylinder(h = 10, d = 35);

    // Bottom joint - connects support to base
    translate([0, 0, 20])
        cylinder(h = 10, d = 35);
}

// Assemble the stool
union() {
    // Golf ball seat
    translate([0, 0, stool_height - ball_diameter/2])
        golfball_surface();

    // Structural elements
    leg_support();
    base();
    structural_joints();

    // Additional support struts
    for (i = [0:2]) {
        rotate([0, 0, i * 120])
            translate([15, 0, (stool_height - ball_diameter/2)/2])
                rotate([90, 0, 0])
                    cylinder(h = 100, d = leg_thickness);
    }
}

