            translate([0, 0, -ball_diameter/2 + 2])
                cylinder(h = 4, r = ball_diameter/3, $fn = 32);
        }

        // Leather seam lines
        color(seam_color) {
            // Main seam around equator
            rotate([0, 0, 0])
                rotate_extrude(angle = 360, $fn = 128)
                    translate([ball_diameter/2 - seam_width/2, 0, 0])
                        square([seam_width, seam_width/2]);

            // Six rows of stitching lines
            for(i = [0:30:150]) {
                rotate([0, i, 0]) {
                    rotate_extrude(angle = 360, $fn = 64)
                        translate([ball_diameter/2 - seam_width/2, 0, 0])
                            square([seam_width, seam_width/4]);
                }
            }
        }

        // Quarter seam details
        for(angle = [45, 135, 225, 315]) {
            rotate([0, 0, angle]) {
                rotate([0, 90, 0])
                    cylinder(h = ball_diameter * 0.8, r = seam_width/2, center = true, $fn = 16);
