module shot_glass_rack() {
    // Base platform with rounded edges
    difference() {
        translate([0, 0, wall_thickness/2])
            minkowski() {
                cube([rack_length, rack_width, wall_thickness], center=true);
                cylinder(h=0.1, r=wall_thickness*2);
            }

        // Recessed bottom for material saving
        translate([0, 0, -wall_thickness/2])
            cube([rack_length-wall_thickness*4, rack_width-wall_thickness*4, wall_thickness*2], center=true);
    }

    // Side supports
    for (x = [-rack_length/2 + wall_thickness*3, rack_length/2 - wall_thickness*3]) {
        translate([x, 0, rack_height/2])
            rounded_support();
    }

    // Cross beams with decorative details
    for (y = [-rack_width/3, rack_width/3]) {
        translate([0, y, rack_height*0.6])
            cross_beam();
    }

    // Glass holders (6 positions)
    for (i = [0:5]) {
        x_pos = -rack_length/2 + 20 + i * (glass_diameter + glass_spacing);
        translate([x_pos, 0, rack_height*0.4])
            glass_holder();
    }

    // Back support for stability
    translate([0, rack_width/2 - wall_thickness, rack_height/2])
        back_support();
