module rounded_support() {
    // Tapered support with gentle curves
    linear_extrude(height=rack_height-wall_thickness, center=true, scale=[0.8,1])
        circle(d=wall_thickness*4);

    // Decorative groove
    for (h = [rack_height*0.2, rack_height*0.5]) {
        translate([0, 0, h - rack_height/2])
            cylinder(h=1, d=wall_thickness*4.2, center=true);
