        translate([0, 0, 0])
            rotate([90, 0, 0])
                cylinder(h = head_height + 2, r1 = handle_diameter/2 + 1, r2 = handle_diameter/2 + 0.5, center = true);

        // Chamfer edges for realistic wood appearance
