            rotate([0, 90, 0])
                cylinder(h = 6, r1 = 2, r2 = 0);

        translate([-head_width/2 + 3, 0, head_height/2 - 2])
            rotate([0, 90, 0])
                cylinder(h = 6, r1 = 2, r2 = 0);
