fabric_bulge = 1.05;  // Fabric stretches over foam

module pad_leg() {
    // Wooden legs with metal cap
    translate([0, 0, leg_height/2]) {
        difference() {
            // Main leg cylinder
            cylinder(h=leg_height, d=8, center=true);

            // Hollow center for weight reduction
            cylinder(h=leg_height+1, d=4, center=true);
        }

        // Metal foot cap
        translate([0, 0, leg_height/2-1]) {
            cylinder(h=2, d=10, center=true);
        }
    }
}

module single_pad(vertical_pos = 0) {
    // Base foam core
    translate([0, 0, vertical_pos + pad_height/2]) {
        // Foam cushion
        color("beige") {
            scale([foam_shrink, foam_shrink, 1]) {
                cube([pad_width, pad_depth, pad_height], center=true);
            }
        }

        // Fabric covering
        color("gray") {
            scale([fabric_bulge, fabric_bulge, 1.1]) {
                cube([pad_width, pad_depth, pad_height], center=true);
            }
        }

        // Tufted buttons
        for(x = [-30, 0, 30]) {
            for(y = [-20, 0, 20]) {
                translate([x, y, vertical_pos + pad_height/2 + 8]) {
                    color("black") {
                        sphere(d=6);
                    }
                }
            }
        }

        // Side seam detail
        translate([0, 0, vertical_pos]) {
            difference() {
                cube([pad_width+2, pad_depth+2, 2], center=true);
                cube([pad_width-2, pad_depth-2, 3], center=true);
            }
        }
    }
}

module pads_furniture() {
    // Main structure
    union() {
        // Stack of pads
        for(i = [0:stack_height-1]) {
            single_pad(i * (pad_height - 5));  // Slight compression in stack

            // Connection zipper between pads
            if(i < stack_height-1) {
                translate([0, -pad_depth/2 - 2, i * (pad_height - 5) + pad_height]) {
                    rotate([90, 0, 0]) {
                        difference() {
                            cylinder(h=40, d=4, center=true);
                            cylinder(h=41, d=2, center=true);
