                }
            }

            // Cross supports
            color("brown") {
                translate([0, -pad_depth/2 + 10, -leg_height/2 + 2]) {
                    cube([pad_width-20, 4, 4], center=true);
                }
                translate([-pad_width/2 + 10, 0, -leg_height/2 + 2]) {
                    cube([4, pad_depth-20, 4], center=true);
                }
            }
        }

        // Handle on side
        translate([pad_width/2 + 5, 0, stack_height * (pad_height - 5)/2]) {
            rotate([0, 90, 0]) {
                difference() {
                    cylinder(h=30, d=8, center=true);
                    cylinder(h=31, d=5, center=true);
                }
            }
        }
    }
}

