// --- Modules -------------------------------------------------------------
module rounded_cube(s, r=edge_round) {
    hull() {
        for(dx=[-1,1], dy=[-1,1], dz=[-1,1])
            translate([r*dx, r*dy, r*dz])
                sphere(r=r);
    }
    scale([s.x-2*r, s.y-2*r, s.z-2*r]) cube(r, center=true);
