module sled_runner() {
    hull() {
        for(a=[0:10:90])
            translate([0, -a, -sled_curve*(1-cos(a/90*90))])
                rotate([90,0,0]) cylinder(h=sled_thick, r=sled_thick/2, $fn=8);
