// Wooden Beam Furniture Element - Realistic Style
// A structural beam with mortise and tenon joints and wood grain texture

// Parameters
beam_length = 120;
beam_width = 20;
beam_height = 30;
wall_thickness = 2;
joint_size = 8;
joint_spacing = 40;

// Wood grain texture parameters
grain_depth = 0.5;
grain_spacing = 2;

// Main beam module
module beam() {
    difference() {
        // Main beam body
        union() {
            // Core beam
            cube([beam_length, beam_width, beam_height], center = true);

            // Rounded edges
            translate([beam_length/2 - beam_width/2, 0, beam_height/2 - beam_width/2])
                rotate([0, 90, 0])
                    cylinder(h = beam_width, r = beam_width/2, center = true);

            translate([-beam_length/2 + beam_width/2, 0, beam_height/2 - beam_width/2])
                rotate([0, 90, 0])
                    cylinder(h = beam_width, r = beam_width/2, center = true);

            translate([beam_length/2 - beam_width/2, 0, -beam_height/2 + beam_width/2])
                rotate([0, 90, 0])
                    cylinder(h = beam_width, r = beam_width/2, center = true);

            translate([-beam_length/2 + beam_width/2, 0, -beam_height/2 + beam_width/2])
                rotate([0, 90, 0])
                    cylinder(h = beam_width, r = beam_width/2, center = true);
        }

        // Hollow center for 3D printing
        translate([0, 0, 0])
            cube([beam_length - wall_thickness*2, beam_width - wall_thickness*2, beam_height - wall_thickness*2], center = true);
    }
}

// Mortise joints (slots for connections)
module mortise_joints() {
    for(i = [-1, 1]) {
        translate([i * joint_spacing, 0, beam_height/2])
            cube([joint_size, beam_width + 2, joint_size/2], center = true);

        translate([i * joint_spacing, 0, -beam_height/2])
            cube([joint_size, beam_width + 2, joint_size/2], center = true);
    }
}

// Tenon joints (protruding connectors)
module tenon_joints() {
    for(i = [-1, 1]) {
        translate([i * (beam_length/2 + joint_size/2), 0, 0])
            cube([joint_size, beam_width - 4, joint_size], center = true);
    }
}

// Wood grain texture
module wood_grain() {
    for(i = [-beam_length/2 + grain_spacing : grain_spacing : beam_length/2 - grain_spacing]) {
        translate([i, 0, beam_height/2 + 0.1])
            cube([grain_spacing/2, beam_width + 1, grain_depth], center = true);
    }
}

// Support brackets
module support_brackets() {
    for(i = [-1, 1]) {
        translate([i * (beam_length/2 - 15), 0, -beam_height/2 - 5])
            difference() {
                // Bracket body
                union() {
                    cube([10, beam_width - 4, 10], center = true);
                    translate([5, 0, -5])
                        rotate([0, 45, 0])
                            cube([14.14, beam_width - 4, 2], center = true);
                }

                // Lightening holes
                for(j = [-3, 0, 3]) {
                    translate([j, 0, -2])
                        cylinder(h = beam_width - 2, r = 1.5, center = true);
                }
            }
    }
}

// Assembly
union() {
    // Main beam structure
    difference() {
        beam();
        mortise_joints();
    }

    // Tenon connectors
    tenon_joints();

    // Surface details
    wood_grain();

    // Support hardware
    support_brackets();
}

// Medium-complexity realistic vault (safe) furniture item
// External size: 100 Ã— 80 Ã— 120 mm (WxDxH)
// Wall thickness: 2 mm
// Designed for 3D printing: no overhangs >45Â°, split into two printable parts

// Parameters
wall = 2;
body_w = 100;
body_d = 80;
body_h = 100;
door_w = body_w - wall*2;
door_h = body_h*0.8;
corner_r = 4;
handle_r = 5;
handle_l = 25;
bolt_num = 5;
bolt_r = 3;
bolt_len = 6;

// Main body
module vault_body() {
    difference() {
        // Outer shell
        minkowski() {
            cube([body_w, body_d, body_h], center = true);
            sphere(r = corner_r);
        }
        // Inner cavity
        minkowski() {
            cube([body_w-wall*2, body_d-wall*2, body_h-wall*2], center = true);
            sphere(r = corner_r-wall);
        }
        // Front cutout for door
        translate([0, body_d/2-wall/2, body_h*0.1])
            cube([door_w+wall, wall+0.1, door_h], center = true);
    }
}

// Door panel
module vault_door() {
    difference() {
        // Door slab
        translate([0, body_d/2-wall/2, body_h*0.1])
            cube([door_w, wall, door_h], center = true);
        // Recessed panel
        translate([0, body_d/2+0.1, body_h*0.1])
            cube([door_w*0.9, wall+0.2, door_h*0.9], center = true);
    }
    // Hinges
    for (z = [-door_h*0.35, door_h*0.35]) {
        translate([-door_w/2-handle_r*0.5, body_d/2-wall/2, body_h*0.1+z])
            rotate([0, 90, 0])
                cylinder(h = wall*2, r = handle_r*0.4, center = true);
    }
    // Bolts
    for (i = [0:bolt_num-1]) {
        angle = i*360/bolt_num;
        x = cos(angle)*(door_w*0.45);
        y = sin(angle)*(door_h*0.45);
        translate([x, body_d/2-wall/2, body_h*0.1+y])
            rotate([90, 0, 0])
                cylinder(h = bolt_len, r = bolt_r);
    }
    // Handle
    translate([0, body_d/2+handle_l/2, body_h*0.1])
        rotate([90, 0, 0])
            cylinder(h = handle_l, r = handle_r);
    // Handle grip
    translate([0, body_d/2+handle_l, body_h*0.1])
        sphere(r = handle_r*1.2);
}

// Base frame with feet
module vault_base() {
    // Base plate
    translate([0, 0, -body_h/2-wall])
        cube([body_w+10, body_d+10, wall*2], center = true);
    // Feet
    foot_x = body_w/2 - 10;
    foot_y = body_d/2 - 10;
    for (x = [-foot_x, foot_x])
        for (y = [-foot_y, foot_y])
            translate([x, y, -body_h/2-wall*2])
                cylinder(h = wall*2, r = 5);
}

// Assembly
union() {
    vault_base();
    vault_body();
    vault_door();
}

