        difference() {
            cube([8, 4, 12]);
            cylinder(h=12, d=4);
        }
    }

    // Right support
    translate([50, -15, 0]) {
        mirror([1, 0, 0]) difference() {
            cube([8, 4, 12]);
            cylinder(h=12, d=4);
        }
