// Spring mechanism
module spring_mechanism() {
    // Main coil spring
    translate([0, 0, 12]) {
        rotate([90, 0, 0]) {
            difference() {
                cylinder(h=8, d=6, center=true);
                translate([0, 0, -1]) cylinder(h=10, d=3, center=true);
            }

            // Spring coils
            for(i=[-3:3]) {
                translate([0, 0, i*1.5]) {
                    difference() {
                        cylinder(h=1.5, d=6, center=true);
                        cylinder(h=1.5, d=4, center=true);
                    }
                }
            }
        }
    }

    // Spring caps
    translate([0, 0, 8]) cylinder(h=2, d=8, center=true);
    translate([0, 0, 16]) cylinder(h=2, d=8, center=true);
}

// Hinge assembly
