    springboard_base();

    // Support structure
    translate([0, 0, 0]) {
        frame_support();
    }

    // Spring and hinge
    translate([0, 0, 0]) {
        spring_mechanism();
    }

    translate([0, 0, 0]) {
        hinge_assembly();
    }

    // Mounting hardware
    translate([0, 0, 0]) {
